content:
[[div class="{$module-name}"]]
[[include :ah-sandbox:acbs
|lang= {$lang}
|item-number= {$item-number}
|clearance= {$clearance}
|container-class= {$container-class}
|secondary-class= {$secondary-class}
|disruption-class= {$disruption-class}
|risk-class= {$risk-class}
]]
[[/div]]
[[module CSS]]
/* 文本栏 */
.contain-class .class-text {
    color: var(--class-color-1);
}

.disrupt-class .class-text {
    color: var(--class-color-2);
}

.risk-class .class-text {
    color: var(--class-color-3);
}

.anom-bar-container .text-part .main-class {
    background-color: var(--color-b-1);
    border-left: 0.5rem solid var(--color-a-1);
}

.anom-bar-container .text-part .disrupt-class {
    background-color: var(--color-b-2);
    border-left: 0.5rem solid var(--color-a-2);
}

.anom-bar-container .text-part .risk-class {
    background-color: var(--color-b-3);
    border-left: 0.5rem solid var(--color-a-3);
}

/* 图标栏 */

.anom-bar-container .danger-diamond > .top-icon::before {
    background-image: var(--icon-1-link);
    background-repeat: repeat-x;
    background-size: contain;
    background-color: var(--color-c-1);
}

.anom-bar-container .danger-diamond > .left-icon::before {
    background-image: var(--icon-2-link);
    background-repeat: repeat-x;
    background-size: contain;
    background-color: var(--color-c-2);
}

.anom-bar-container .danger-diamond > .right-icon::before {
    background-image: var(--icon-3-link);
    background-repeat: repeat-x;
    background-size: contain;
    background-color: var(--color-c-3);
}

.anom-bar-container .danger-diamond > .bottom-icon::before {
    background-image: var(--icon-4-link) !important;
    background-repeat: repeat-x;
    background-size: contain;
    background-color: var(--color-c-4);
}

.anom-bar-container .text-part > .main-class::before {
    background-image: var(--icon-1-link);
    background-repeat: repeat-x;
    background-color: var(--color-c-1);
}

.anom-bar-container .text-part > .main-class::after {
    background-image: var(--icon-4-link) !important;
    background-repeat: repeat-x;
    background-color: var(--color-c-4);
}

.anom-bar-container .text-part .disrupt-class::after {
    background-image: var(--icon-2-link);
    background-repeat: repeat-x;
    background-color: var(--color-c-2);
}

.anom-bar-container .text-part .risk-class::after {
    background-image: var(--icon-3-link);
    background-repeat: repeat-x;
    background-color: var(--color-c-3);
}

.anom-bar-container .text-part .disrupt-class::before {
    content: var(--level-2);
}

.anom-bar-container .text-part .risk-class::before {
    content: var(--level-3);
}

/* 机密条区 */

.anom-bar-container .top-right-box > .clearance::before {
    content: var(--level-1);
}

.anom-bar-container .top-center-box > div {
    background: var(--level-color);
}

.anom-bar-container .top-center-box > .bar-one {
    display: var(--cbar-1);
}

.anom-bar-container .top-center-box > .bar-two {
    display: var(--cbar-2);
}

.anom-bar-container .top-center-box > .bar-three {
    display: var(--cbar-3);
}

.anom-bar-container .top-center-box > .bar-four {
    display: var(--cbar-4);
}

.anom-bar-container .top-center-box > .bar-five {
    display: var(--cbar-5);
}

.anom-bar-container .top-center-box > .bar-six {
    display: var(--cbar-6);
}
[[/module]]
[[module CSS]]
.{$module-name} {
--class-color-1: {$container-class-name-color};
--class-color-2: {$disruption-class-name-color};
--class-color-3: {$risk-class-name-color};

--level-color: {$clearance-level-color};

--color-a-1: {$tb-left-color-1};
--color-a-2: {$tb-left-color-2};
--color-a-3: {$tb-left-color-3};

--color-c-1: {$icon-bg-color-1};
--color-c-2: {$icon-bg-color-2};
--color-c-3: {$icon-bg-color-3};
--color-c-4: {$icon-bg-color-4};

--color-b-1: {$tb-main-color-1};
--color-b-2: {$tb-main-color-2};
--color-b-3: {$tb-main-color-3};

--level-1: "{$clearance-level}";
--level-2: "{$disruption-level}";
--level-3: "{$risk-level}";

--cbar-1: {$clearance-1};
--cbar-2: {$clearance-2};
--cbar-3: {$clearance-3};
--cbar-4: {$clearance-4};
--cbar-5: {$clearance-5};
--cbar-6: {$clearance-6};

--icon-1-link: url("{$container-icon}");
--icon-2-link: url("{$disruption-icon}");
--icon-3-link: url("{$risk-icon}");
--icon-4-link: url("{$esoteric-icon}");

--secondary-class: {$secondary-class};
}
[[/module]]
