content:
[[module CSS]]
.wide {
  display: block;
}

.mobile {
  display: none;
}

@media (max-width: 1550px) {
  .wide {
    display: none;
  }

  .mobile {
    display: block;
  }

  #main-content {
    margin-top: 170px;
  }
}

@media (max-width: 1550px) and (min-width: 768px) {
  #side-bar {
    margin-top: -150px;
  }
}
[[/module]]

[[a href="*{$url}" class="wide" style="position: absolute; border: 1px solid lightgrey; background-image: url("{$image1}"); background-size: cover; background-repeat: no-repeat; background-position: center; width: 200px; height: 598px; right: 0; transform: translate(250px, -50px);"]]

[[/a]]


[[a href="*{$url}" class="mobile" style="position: absolute; border: 1px solid lightgrey; background-image: url("{$image2}"); background-size: cover; background-repeat: no-repeat; background-position: center; width: 100%; height: 150px; top: 0; transform: translateY(-155px);"]]

[[/a]]
