content:
[[module css]]
div#u-adult-warning {
  width: fit-content;
  margin: 0 auto 20px;
  background: #ffffff;
  border: 3px solid #333;
}

div#u-adult-warning>.error-block {
  color: unset;
  padding: unset;
  margin: unset;
  border: unset;
  margin-bottom: 1em
}

#page-content {
  position: relative;
}
#page-content ul.modal-wrapper:not(.yui-nav) {
  margin: 0;
  padding: 0;
  list-style: none;
  height: 0;
  display: contents;
}
@keyframes fademodal {
  99% {
    visibility: hidden;
  }
  100% {
    visibility: visible;
  }
}
ul.modal-wrapper>li {
  position: absolute;
  top: -0.1rem;
  left: 0;
  width: 100%;
  height: 100%;
  z-index: 9;
  transition: opacity 0.15s ease-out;
  animation: 0.15s fademodal;
  animation-fill-mode: forwards;
}
ul.modal-wrapper>li.unfolded {
  visibility: visible;
  opacity: 1;
}
ul.modal-wrapper>li.folded {
  visibility: hidden;
  opacity: 0;
  pointer-events: none;
  user-select: none;
}
div#u-adult-warning {
  width: 100%;
  height: 100%;
  overflow: auto;
  box-sizing: border-box;
  color: black;
  text-shadow: none;
}

#page-content div#u-adult-warning a {
  color: #b01;
}
#page-content div#u-adult-warning a.newpage {
  color: #d61;
}
#page-content div#u-adult-warning a:not(.newpage):visited {
  color: #824;
}
#u-adult-warning .choice {
  padding: 0.125em 0;
  pointer-events: none;
}
#u-adult-warning .choice>a {
  pointer-events: initial;
}




/* JP orig */

#page-content .wikisys--template-dialog_a {
    width: 90%;
    max-width: 500px;
    margin: 2em auto;
    padding: 2em;
    background: #f9f9f9;
    border-radius: 2em;
    color: #330011!important;
    border: solid 1px #b7b7b7
}

#page-content .wikisys--template-dialog_a>.wikisys--template-dialog_a-header {
    position: relative;
    line-height: 1.5;
    font-size: 1.3rem;
    font-weight: 700;
    padding: 5px 0 5px 12px;
    box-shadow: -0.375rem 0 #93939333;
}

div#page-options-bottom-2:not([style="display:none"]):before {
    content: "このページは成人向けコンテンツを含むページです。\Aページソースは18歳以上の方だけが閲覧できます。";
    display: flex;
    background-color: #ff2800;
    border: solid #333 5px;
    border-radius: 10px;
    padding: 10px;
    margin: 10px 0;
    color: #fff;
    font-size: 1.5em;
    text-align: left;
    white-space: break-spaces;
}

#page-content ul.modal-wrapper ~ * ul.modal-wrapper.adult_c {
    display: none;
}

[[/module]]


[[ul class="modal-wrapper adult_c"]]
[[li class="unfolded"]]
[[div id="adult-warning"]]
[[div_ class="wikisys--template-dialog_a"]]
[[div_ class="wikisys--template-dialog_a-header"]]
