.w-container {
  padding: 14px;
  margin-bottom: 16px;
  margin-top: 16px;
  border: 1px #bbbbbb solid;
  box-shadow: 1px 2px 2px 0 rgba(0,0,0,0.16);
  box-sizing: border-box;
}
.cell-container {
  display: flex;
  align-items: center;
  justify-content: space-between;
  flex-wrap: wrap;
  width: 100%;
  overflow: hidden;
  font-size: small;
  margin-bottom: -8px;
  margin-top: -8px;
  padding: 0px;
}
.cell-container-image {
  display: flex;
  align-items: center;
  justify-content: space-between;
  flex-wrap: wrap;
  width: 100%;
  overflow: hidden;
  font-size: small;
  margin-bottom: -8px;
  margin-top: -8px;
  padding: 0px;
  background-size: var(--aim-cc-siz), var(--aim-lv-siz);
  background-repeat: no-repeat;
  background-position: 50%,50%;
  background-image: var(--aim-cc-image), var(--aim-lv-image);
}
.s-cell {
  width: 50%;
  font-size: 15px;
  margin-top: -0.4rem;
  margin-bottom: -0.8rem;
}
@media (max-width: 350px) {
.cell-container-image {
   font-size: 12px;
}
.s-cell{
   font-size: 12px;
}
}
.w-cell {
  flex-grow: 2;
  max-width: 50%;
  text-align: center;
  font-size: 0.66rem;
  border-top: 1px solid #ccc;
  border-left: 1px solid #ccc;
  margin-top: -1px;
  margin-left: -1px;
  padding: 2px;
  box-sizing: border-box;
}
.w-cell > hr {
  margin: -2px;
  margin-top: -0.6em;
  background-color: #ccc;
}
.b-cell {
    position: relative;
    padding: 0.5em;
    margin: 0.5em;
    overflow-wrap: break-word;
    color: black;
}

