[[code type="css"]]
@charset "utf-8";
@import url('http://ja.scp-wiki.net/local--files/component:af2017theme/HVD_Bodedo.css');
@import url('http://maxcdn.bootstrapcdn.com/font-awesome/4.3.0/css/font-awesome.min.css');

/*
    [2017 AF Wikidot Theme]
    Created for the SCP Foundation by Aelanna

    [Edited]
    * Devan 
    * C-Take
    * Nanimono_Demonai

	[Original Pens]
	https://blog.codepen.io/legal/licensing/
	* Parallax Star background in CSS PEN-BY Saransh Sinha (https://codepen.io/saransh/pen/BKJun)
	* UFO speeder PEN-BY Louis Coyle (https://codepen.io/dropside/pen/dIymk)

    Deployed for SCP財団 by NanimonoDemonai
*/


/* COMMON */


/* ルビ用 */

body {
	-webkit-text-size-adjust: 110%;
}
.post .content a {
	word-wrap: break-word;
}
#content-wrap {
	position: relative;
	margin: 2em auto 0;
	max-width: 1040px;
	min-height: 1300px;
	height: auto !important;
	height: 1500px;
}
h1, #page-title {
	color: #901;
	padding: 0 0 0.25em;
	margin: 0 0 0.6em;
	font-weight: normal;
}
h1 {
	margin-top: 0.7em;
	padding: 0;
	font-weight: bold;
}
h2, h3, h4, h5, h6, h7 {
	margin: 0.5em 0 0.4em;
	padding: 0;
	letter-spacing: 1px;
}
#page-title {
	border-color: #bbb;
}
ul {
	/* list-style-image: url(http://d3g0gp89917ko0.cloudfront.net/v--3b8418686296/common--theme/shiny/images/bullet.gif); */
	list-style: square;
}
#top-bar ul {
	/* list-style-image: none; /* IE ONLY! IF list-style-image IS SET */
}
li, p {
	line-height: 141%;
}
a {
	color: #b01;
	text-decoration: none;
	background: transparent;
}
a.newpage {
	color: #d61;
	text-decoration: none;
	background: transparent;
}
a:hover {
	text-decoration: underline;
	background-color: transparent;
}
.form-control {
	width: 95%;
}

/* GLOBAL WIDTH */

#header, #top-bar {
	width: 90%;
	max-width: 980px;
	margin: 0 auto;
}
#top-bar {
	width: 100%;
	margin: 0 auto;
}
.mobile-top-bar {
	display: none;
	position: absolute;
	left: 1em;
	bottom: 0px;
	z-index: 0;
}
body {
	background-color: #fff;
	font-size: 0.80em;
	color: #333;
}
div#container-wrap {
	background: url(http://ja.scp-wiki.net/local--files/component:af2017theme/body_bg_af2017.png) top left repeat-x;
}

/* HEADER */

#header {
	height: 140px;
	position: relative;
	z-index: 10;
	padding-bottom: 22px;
	/* FOR MENU */
	background: url(http://ja.scp-wiki.net/local--files/component:af2017theme/logoaf2017.png) 10px 40px no-repeat;
}
#search-top-box {
	position: absolute;
	top: 79px;
	right: 9px;
	width: 250px;
	text-align: right;
}
#search-top-box-input {
	border: solid 1px #999;
	border-radius: 5px;
	color: #ccc;
	background-color: #300;
	box-shadow: inset 1px 1px 3px rgba(0, 0, 0, .5);
}
#search-top-box-input:hover, #search-top-box-input:focus {
	border: solid 1px #fff;
	color: #fff;
	background-color: #633;
	box-shadow: inset 1px 1px 3px rgba(0, 0, 0, .8);
}
#search-top-box-form input[type=submit] {
	border: solid 1px #999;
	border-radius: 5px;
	padding: 2px 5px;
	font-size: 90%;
	font-weight: bold;
	color: #ccc;
	background-color: #633;
	background: linear-gradient(to bottom, #966, #633, #300);
	box-shadow: 0 1px 3px rgba(0, 0, 0, .5);
	cursor: pointer;
}
#search-top-box-form input[type=submit]:hover, #search-top-box-form input[type=submit]:focus {
	border: solid 1px #fff;
	color: #fff;
	text-shadow: 0 0 1px rgba(255, 255, 255, .25) background-color: #966;
	background: linear-gradient(to bottom, #c99, #966, #633);
	box-shadow: 0 1px 3px rgba(0, 0, 0, .8);
}
#login-status {
	color: #aaa;
	font-size: 90%;
	z-index: 30;
	font-family: verdana, arial, helvetica, 'Meiryo', 'Nanum Gothic', sans-serif;
}
#login-status a {
	background: transparent;
	color: #ddd;
}
#login-status ul a {
	color: #700;
	background: transparent;
}
#account-topbutton {
	background: #ccc;
	color: #700;
}
.printuser img.small {
	margin-right: 1px;
}
#header h1 {
	margin-left: 120px;
	padding: 0;
	float: left;
	max-height: 95px;
}
#header h2 {
	margin-left: 120px;
	padding: 0;
	clear: left;
	float: left;
	font-size: 105%;
	max-height: 38px;
}
#header h1 a {
	display: block;
	margin: 0;
	padding: 80px 0 25px;
	line-height: 0px;
	max-height: 0px;
	color: #eee;
	background: transparent;
	font-family: hvd_bodedomedium, 'Nanum Gothic', Arial, sans-serif;
	font-size: 180%;
	text-decoration: none;
	text-shadow: 3px 3px 5px #000;
	letter-spacing: 0.9px;
}
#header h2 span {
	display: block;
	margin: 0;
	padding: 19px 0;
	line-height: 0px;
	max-height: 0px;
	font-weight: bold;
	color: #f0f0c0;
	text-shadow: 1px 1px 1px #000;
	text-shadow: 1px 1px 1px rgba(0, 0, 0, .8);
}

/* TOP MENU */

#top-bar {
	position: absolute;
	z-index: 50;
	top: 140px;
	height: 21px;
	line-height: 18px;
	padding: 0;
	z-index: 20;
	font-size: 90%;
}
#top-bar ul {
	float: right;
}
#top-bar li {
	margin: 0;
}
#top-bar a {
	color: #fff;
	background: transparent;
}
#top-bar ul li {
	border: 0;
	position: relative;
}
#top-bar ul li ul {
	border: solid 1px #666;
	box-shadow: 0 2px 6px rgba(0, 0, 0, .5);
	border-top: 0;
}
#top-bar ul li a {
	border-left: solid 1px rgba(64, 64, 64, .1);
	border-right: solid 1px rgba(64, 64, 64, .1);
	text-decoration: none;
	padding-top: 10px;
	padding-bottom: 10px;
	line-height: 1px;
	max-height: 1px;
	overflow: hidden;
}
#top-bar ul li.sfhover a, #top-bar ul li:hover a {
	background: #eee;
	color: #a01;
	border-left: solid 1px rgba(64, 64, 64, 1);
	border-right: solid 1px rgba(64, 64, 64, 1);
}
#top-bar ul li.sfhover ul li a, #top-bar ul li:hover ul li a {
	border-width: 0;
	width: 150px;
	border-top: 1px solid #ddd;
	line-height: 160%;
	height: auto;
