}
.content-panel .panel-footer a {
	color: #ffffff;
}
.content-panel .content-toc {
	float: right;
	padding: 0 20px;
	background-color: #fff;
	border: solid 1px #ccc;
	border-radius: 10px;
	margin: 20px 0 5px 5px;
	white-space: nowrap;
	box-shadow: inset 1px 2px 6px rgba(0, 0, 0, .15)
}
.alternate:nth-child(even) {
	background-color: rgba(255, 255, 255, .9);
}

/* Page Rating Module Customizations */

.page-rate-widget-box {
	display: inline-block;
	border-radius: 5px;
	box-shadow: 1px 1px 3px rgba(0, 0, 0, .5);
	margin-bottom: 10px;
	margin-right: 2em;
}
div.page-rate-widget-box .rate-points {
	background-color: #633;
	border: solid 1px #633;
	border-right: 0;
	border-radius: 5px 0 0 5px;
}
.page-rate-widget-box .rateup, .page-rate-widget-box .ratedown {
	background-color: #fff6f0;
	border-top: solid 1px #633;
	border-bottom: solid 1px #633;
	font-weight: bold;
}
.page-rate-widget-box .rateup a, .page-rate-widget-box .ratedown a {
	background: transparent;
	color: #633;
	padding: 0 4px;
	margin: 0 1px;
}
.page-rate-widget-box .rateup a:hover, .page-rate-widget-box .ratedown a:hover {
	background: #633;
	color: #fffff0;
	text-decoration: none;
}
.page-rate-widget-box .cancel {
	background-color: #633;
	border: solid 1px #633;
	border-left: 0;
	border-radius: 0 5px 5px 0;
}
.page-rate-widget-box .cancel a {
	background: transparent;
	text-transform: uppercase;
	color: #966;
}
.page-rate-widget-box .cancel a:hover {
	border-radius: 0 3px 3px 0;
	background: #633;
	color: #fffff0;
	text-decoration: none;
}

/* Heritage Collection Rating Module */

.heritage-rating-module {
	float: right;
	background-color: #633;
	padding: 2px 8px 2px 5px;
	border: solid 1px #ccc066;
	border-radius: 8px;
	box-shadow: 0 1px 3px rgba(0, 0, 0, .25);
}
.heritage-rating-module .page-rate-widget-box {
