@charset "utf-8";

/* 
    ZZZ theme
    [2019 Wikidot theme]
    Created by 7happy7, WotaliaSD, Dr Devan, NanimonoDemonai
    for af2019 in SCP-JP
    CC BY-SA 3.0
 */

body  {
    color: #730000;
}
a {
    color: hsla(0, 100%, 38%, 1);
}

div.open-menu a {
    color: #730000!important;
    border-color: #730000!important;
}

/* Z 본체 */
div#header h2 span:before,
div#header h2 span:after {
    content: "Z";
    font-size: calc(180% * 1.9 / 1.05);
    color: #eee;
    background: transparent;
    font-family: BauhausLTDemi, Arial, sans-serif;
    text-shadow: 3px 3px 5px #000;
    letter-spacing: 0.9px;
    position: absolute;
    top: 66px;
    left: 0;
    right: 0;
    margin: 0 auto;
    opacity: 0;
    pointer-events: none;
}
/* Z의 움직임 */
div#header h2 span:before {
    animation: smoky 5s 0s infinite both;
}
div#header h2 span:after {
    animation: smoky-mirror 5s 2.5s infinite both;
}
/* Z의 움직임 정의 */
@keyframes smoky {
  10% {
    text-shadow: 0 0 0 #666;
    color: #ddd;
    opacity: 1;
  }
  60% {
    text-shadow: 0 0 40px #666;
    color: #ddd;
    opacity: 0;
  }
  to {
    transform: translate3d(240px,-128px,0) rotate(-40deg) skewX(70deg) scale(1.5);
    text-shadow: 0 0 20px #666;
    color: #ddd;
    opacity: 0;
  }
}
@keyframes smoky-mirror {
  10% {
    text-shadow: 0 0 0 #666;
    color: #ddd;
    opacity: 1;
  }
  60% {
    text-shadow: 0 0 40px #666;
    color: #ddd;
    opacity: 0;
  }
  to {
    transform: translate3d(288px,-128px,0) rotate(-40deg) skewX(-70deg) scale(2);
    text-shadow: 0 0 20px whitesmoke;
    opacity: 0;
  }
}

/* 
    꽃무늬 by WotaliaSD
    헤더 by NanimonoDemonai
 */
div#container-wrap {
    background: url(https://scp-jp-sandbox3.wdfiles.com/local--files/draft%3A7happy7-27-0a13/body_bg_4_1.png) top left repeat-x, url("https://scp-jp-sandbox3.wdfiles.com/local--files/draft%3A7happy7-27-0a13/often.png");
}

/* 제목의 한가운데에서 밀려나옴 예시 */
#header h1 {
    margin: 0 auto;
    float: none;
    text-align: center;
}
#header h2 {
    pointer-events: none;
    margin: 0 auto;
    clear: both;
    float: none;
    text-align: center;
    width: 100%;
    height: 162px;
    min-height: 162px;
    position: absolute;
    top: 0;
    overflow: hidden;
}
/* SCP재단을 비표시함 */
div#header h1 a span {
    font-size: 0px;
}
/* 확보, 격리, 보호를 비표시함 */
div#header h2 span {
    color: transparent;
    text-shadow: none;
    padding: 0;
}
/* 대체 제목 */
div#header h1 a:before {
    content: "SCP 이불";
    color: #eee;
}
div#header h1 a:after {
    content: "깨우지 마세요";
    display: block;
