[[module CSS]]
@import url("https://scp-jp.github.io/files/component/af2020theme/style0.css");
#page-title {
    display: none;
}
.feature-block div.panel-body {
    min-height: 180px;
    height: unset;
    overflow-y: unset;
}
.feature-block {
    margin-bottom: 3rem;
}
.sp .feature-block {
    display: none;
}
.international-block .panel-body {
    padding: 0;
}
.international-entry {
    padding: 5px 10px;
}
.international-entry:nth-child(even),
.alternate:nth-child(even) {
    background-color: rgba(255,255,255,.9);
    border-top: solid 1px #eeeee0;
    border-bottom: solid 1px #eeeee0;
}
.international-entry div > p {
    margin: 0;
}
.international-entry .international-title {
    font-weight: bold;
    font-size: 110%;
}
.international-entry .international-title img {
    width: 16px;
    height: 11px;
    box-shadow: 0 1px 3px rgba(0,0,0,.5);
}
.international-entry .international-description {
    font-size: 90%;
    font-style: italic;
    margin: 5px 20px;
}
.international-entry .international-footer {
    font-size: #333;
    font-size: 80%;
    margin: 5px 0;
}
[[/module]]


