[[module CSS]]
@import url('https://fonts.googleapis.com/css2?family=Outfit:wght@500&display=swap');
@import url('https://fonts.googleapis.com/css2?family=Noto+Sans+JP:wght@500&display=swap');

/*
  [EN]
  SCP-JP 10th ANNIVERSARY EVENTS: April Fool 2023 "Super Celebration Party" Theme
  Creative Commons 3.0 Attribution Succession
  Managed by SCP-JP 10th Anniversary Event Committee and AF-2023-JP Project Committee
  Created at 2023 by Mishary
  Edited by

  [JA]
  SCP-JP10周年イベント: エイプリルフール2023「Super Celebration Party」テーマ
  クリエイティブコモンズ3.0 表示 継承
  テーマ管理者: SCP-JP10周年イベント委員会、AF-2023-JP企画委員
  作成年: 2023
  テーマ作成者: Mishary
  その他の編集者: 
*/

/* COMMON */

body {
    font-family: 'Outfit', 'Noto Sans JP', sans-serif;
    font-weight: bold;
    background-color: #fff;
}

#content-wrap {
    max-width: 1200px;
}

#page-title, h1, h2, h3, h4, h5, h6 {
    color: #000;
    font-family: 'Outfit', 'Noto Sans JP', sans-serif;
}

#page-title {
    font-weight: bold;
}

.footnote-footer {
     overflow: auto;
}

a {
    color: #ed3442;
}
a:visited {
    color: #ed3442;
}

#toc, blockquote{
    border-color: #9fa0a0;
    border-style: solid;
    border-width: 1px;
    margin: 1em 0;
    background-color: rgba(223, 224, 211,.5);
}

.scp-image-block .scp-image-caption{
    background-color: rgba(223, 224, 211,.5);
}

#page-content {
    background: #fffa;
}

#page-content hr {
    background: #000;
    margin: 1em 0;
    width: 100%;
}

/* GLOBAL WIDTH */
div#container-wrap {
    background: none;
}

/* HEADER */
#header {
    background: url(http://scp-jp-storage.wikidot.com/local--files/file:5818807-14-bcqs/JP-10th-birthcake) 10px 30px no-repeat;
    background-size: 100px;
    height: 120px;
    max-width: 1140px;
}
#header h1 a {
    text-shadow: unset;
    font-family: 'Outfit', 'Noto Sans JP', sans-serif;
}
#header h1 a span {
    font-size: 0px;
}
#header h1 a::before {
    color: #000;
    content: "SCP祭典";
}
#header h2 span {
    font-family: 'Outfit', 'Noto Sans JP', sans-serif;
    font-size: 0px;
    text-shadow: unset;
}
#header h2 span::before {
    content: "Super Celebration Party";
    color: #000;
    font-size: 12px;
}

#search-top-box {
    top: 60px;
}
#search-top-box-input {
    background-color: transparent;
    color: #000;
}
#search-top-box input.empty{
    color: #000;
    border-radius: unset;
}
#search-top-box-input:hover, #search-top-box-input:focus {
    background-color: rgba(102,51,51,.2);
    color: #000;
}
#search-top-box-form input[type=submit] {
    border: none;
    color: rgba(223, 224, 211,.5);
    background: #000;
    border-radius: unset;
}
#search-top-box-form input[type=submit]:hover, #search-top-box-form input[type=submit]:focus {
    border: none;
    color: #000;
    text-shadow: 0 0 1px rgba(255,255,255,.25);
    background: rgba(223, 224, 211,.5);
    box-shadow: 0 1px 3px rgba(0,0,0,.8);
}

.printuser{
    color: #000;
}

#login-status{
    color: #000;
}

#login-status a{
    color: #000;
}

/* TOP MENU */
#top-bar {
    top: 120px;
    max-width: 1140px;
}

#top-bar a {
    color: #000;
}

#top-bar li:hover ul, #top-bar li.sfhover ul {
    box-shadow: unset;
}

#top-bar ul li.sfhover a, #top-bar ul li:hover a {
    color: #000;
    background: #fff;
}

#top-bar ul li.sfhover a:hover, #top-bar ul li:hover a:hover{
    color: #fff;
    background: #ed3442;
}
/* SIDE MENU */
#side-bar .side-block {
    border-bottom-color: #ed3442;
    border: none;
    margin-bottom: 0;
    padding: 12px 0px;
    background: none;
    background-color: transparent !important;
    box-shadow: none;
}
#side-bar .side-block a, #side-bar .collapsible-block-unfolded-link .collapsible-block-link{
    color: #000;
    font-size: 9pt;
}

#side-bar .collapsible-block-link {
    margin-left: 17px;
}
#side-bar .side-block a:hover, #side-bar .collapsible-block-unfolded-link .collapsible-block-link:hover{
    color: #ed3442;
    text-decoration: none;
}
#side-bar .side-block+ .side-block {
    border-top: 1px solid #9fa0a0;
    border-radius: 0;
}
#side-bar .side-block hr {
    display: none;
}
#side-bar hr {
    margin: 0;
    background-color: #ed3442;
}
#side-bar .heading {
    border-top: solid 1px #ed3442;
    border-bottom: none;
    margin-top: 12px;
    padding-top: 12px;
    padding-left: 16px;
    color: #000;
    font-size: 9pt;
    -webkit-text-stroke-width: 0.8px;
}
#side-bar div.menu-item {
    margin: 2px 0 2px 12px;
    padding-left: 4px;
}
#side-bar div.menu-item a {
    font-size: 9pt;
    font-weight: bold;
    line-height: 23px;
}
#side-bar .collapsible-block-unfolded-link {
    border-bottom: solid 1px #ed3442;
    padding-bottom: 12px;
}
.side-block .menu-item > .image {
    filter: hue-rotate(12deg) saturate(21%) brightness(96%);
}

#interwiki body {
    background: unset;
}

#side-bar .side-block {
    background: #fffa !important;
    border: unset;
    border-radius: unset;
    box-shadow: unset;
}

/* Rate Module */
div.page-rate-widget-box .rate-points, .page-rate-widget-box .cancel, .rate-box-with-credit-button .creditButton {
    background: #000!important;
    border: none;
    border-radius: unset;
    color: #fff;
}
.page-rate-widget-box .cancel a{
    color: #fff;
}
.creditButton a, .creditButton a:visited{
    color: #fff
}
.page-rate-widget-box .cancel a:hover{
    background: #fff;
    color: #ed3442;
    border-radius: unset;
}
.page-rate-widget-box .rateup, .page-rate-widget-box .ratedown{
    background: #fff;
    border: none;
    color: #000;
}
.page-rate-widget-box .rateup a, .page-rate-widget-box .ratedown a{
    color: #000;
}
.page-rate-widget-box .rateup a:hover, .page-rate-widget-box .ratedown a:hover{
    background: #ed3442;
    color: #fff;
}

.modalbox {
    border-radius: unset;
}
.modalbox .modalbox-title {
    border-radius: unset;
    background: #000;
}
.modalbox .modalbox-title h2{
    color: #fff;
}

@keyframes snow {
    0% {
        background-position: 0px 0px, 0px 0px, 0px 0px;
    }
    100% {
        background-position: -400px 800px, 320px 320px, 0px 240px;
    }
}

body {
    background-image: url("http://scp-jp-storage.wikidot.com/local--files/file:3438721-263-o6so/af2023_side.png"), url("http://scp-jp-storage.wikidot.com/local--files/file:3438721-263-o6so/af2023_side.png");
    background-size: 100px 1600px;
    background-position: left 800px, right -100px;
    background-repeat: repeat-y;
}

#skrollr-body{
    background-image:
        url("http://scp-jp-storage.wikidot.com/local--files/file:3438721-262-iozc/kamifubuki.png"),
        url("http://scp-jp-storage.wikidot.com/local--files/file:3438721-262-iozc/kamifubuki.png"),
        url("http://scp-jp-storage.wikidot.com/local--files/file:3438721-262-iozc/kamifubuki.png");
    background-size: 400px 400px, 320px 320px, 240px 240px;
    background-repeat: repeat;
    animation: snow 30s linear infinite;
}


.strengthening-month {
    position: relative;
    padding: 15px 0 15px 0;
    text-align: center;
}
.strengthening-month a {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
}

.content-panel {
    box-shadow: unset;
    border-radius: unset;
}

/* Media */
@media (min-width: 768px) and (max-width: 979px) {
  #header {
    background-position-y: 10px;
  }
  #header h1, #header h2 {
    margin-left: 120px;
  }
  #header h1 a {
    padding: 65px 0 10px;
  }
}

@media (max-width: 767px) {
  #header {
    background-position-y: 0;
    height: 90px;
  }
  #header h1, #header h2 {
    margin-left: 120px;
  }
  #header h1 a {
    padding: 55px 0 10px;
  }
  #top-bar {
    top: 90px;
  }
  div.scpnet-interwiki-wrapper {
    width: auto;
  }
  #side-bar {
    background-color: #fff;
  }
  #side-bar:target {
    border: 1px solid #9fa0a0;
  }
  #side-bar:target .close-menu {
    background: rgba(0, 0, 0, 0.1) 1px 1px repeat;
  }
}

@media (max-width: 581px){
  #header {
    background-position-x: 0;
  }
  #header h1, #header h2 {
    margin-left: 105px;
  }
  #header h1 a {
    font-size: 120%;
  }
}

@media (min-width: 479px) and (max-width: 767px) {
  .mobile-top-bar {
    left: unset;
    right: 0;
  }
}

@media (max-width: 479px) {
  #header {
    background-size: 80px;
    height: 80px;
  }
  #header h1, #header h2 {
    margin-left: 90px;
  }
  #header h1 a {
    padding: 45px 0 7px;
  }
  #search-top-box {
    top: 40px;
  }
  #top-bar {
    top: 80px;
  }
}

td.af_thumbnail {
    width: 15%;
}

@media (min-width: 850px) and (max-width: 979px) {
  td.af_thumbnail {
    width: 20%;
  }
}

@media (min-width: 768px) and (max-width: 849px) {
  td.af_thumbnail {
    width: 25%;
  }
}

@media (min-width: 582px) and (max-width: 767px) {
  td.af_thumbnail {
    width: 20%;
  }
}

@media (min-width: 480px) and (max-width: 581px) {
  td.af_thumbnail {
    width: 25%;
  }
}

@media (max-width: 479px) {
  td.af_thumbnail {
    width: 30%;
  }
}
