[[module css]]

.unmargined {
    color: rgb(255 255 255 / 80%);
}

.unmargined>p {
    text-shadow: 0 0 3px #ff4747, 0 0 5px #ff4747, 0 0 6px #ff4747, 0 0 19px #ff4747, 0 0 10px #ff4747;
}


div#container {
    position: relative;
}
div#container:after {
    /* content: ""; */
    filter: blur(2px);
    mix-blend-mode: saturation;
    background-image: repeating-linear-gradient(90deg, #e618ff, #e618ff 5%, rgba(0, 0, 0, 0) 5.1%, rgba(0, 0, 0, 0) 100%), repeating-linear-gradient(0deg, #e618ff , #e618ff 5%, rgba(0, 0, 0, 0) 5.1%, rgba(0, 0, 0, 0) 100%);
    position: absolute;
    background-size: 8em 8em;
    background-position: 0 0;
    -webkit-animation: the80sFloor 0.6s linear infinite;
    animation: the80sFloor 0.6s linear infinite;
    will-change: animation;
    transform: translate(-42%, 0%) rotateX(76deg);
    transform-style: preserve-3d;
    width: 500em;
    height: 200em;
    pointer-events: none;
    left: 0;
    bottom: 0
}


@keyframes the80sFloor  {
    100% {
        background-position: 0 8em;
    }
}

div#container:before {
    /* content: ""; */
    filter: blur(2px);
    mix-blend-mode: overlay;
    background-image: repeating-linear-gradient(90deg, #74098280, #74098280 5%, rgba(0, 0, 0, 0) 5.1%, rgba(0, 0, 0, 0) 100%), repeating-linear-gradient(0deg, #74098280, #74098280 5%, rgba(0, 0, 0, 0) 5.1%, rgba(0, 0, 0, 0) 100%);
    position: absolute;
    background-size: 8em 8em;
    background-position: 0 0;
    -webkit-animation: the80sFloor 0.6s linear infinite;
    animation: the80sFloor 0.6s linear infinite;
    will-change: animation;
    transform: translate(-42%, -30%) rotateX(86deg);
    transform-style: preserve-3d;
    width: 500em;
    height: 200em;
    pointer-events: none;
    left: 0;
    bottom: 0;
}


:has(#page-content .colmod-block.switch .unfolded) div#container {
    perspective: 100vh;
}

:has(#page-content .colmod-block.switch .unfolded) div#container:before, :has(#page-content .colmod-block.switch .unfolded) div#container:after{
     content: "";
}

[[/module]]

[[module css]]
.colmod-block.switch .foldable-list-container {
  display: flex;
}

.colmod-block.switch .foldable-list-container a {
  appearance: none;
  font-size: 1em;
  width: 3em;
  aspect-ratio: 2;
  border: 1px solid;
  border-radius: 2em;
  background: #fff;
  position: relative;
}
.colmod-block.switch .foldable-list-container a::before {
  content: "";
  position: absolute;
  top: 50%;
  left: 25%;
  transform: translate(-50%, -50%);
  width: 0.8em;
  height: 0.8em;
  box-sizing: border-box;
  border: 1px solid rgba(0, 0, 0, 0);
  border-radius: 50%;
  background: currentcolor;
  transition: left 0.5s;
}
.colmod-block.switch .foldable-list-container a:last-child::before {
  left: 75%;
}


@media (prefers-reduced-motion) {
  .foldable-list-container a::before {
    transition-duration: 0s;
  }
}
.colmod-block.switch {
    position: absolute;
    right: 0;
}

@media screen and (max-width: 768px) {
  #html-body .colmod-block.switch {
    display: none;
  }
}
[[/module]]
[[/iftags]]





