[[code type="CSS"]]
/*
    SCP-JP 2024AF Theme
    Created at 2024
    Created by r74
    CC BY-SA 3.0

    [no title]
    Created at 2021
    Created by dog_punch
    CC BY-SA 3.0
*/

@import url('https://fonts.googleapis.com/css2?family=Nixie+One&display=swap');
@import url('https://fonts.googleapis.com/css2?family=Noto+Sans+JP:wght@100;300;400&display=swap');
div#container-wrap {
    background: none;
}
#extra-div-1,
.neon_titles,
#top-bar div>ul,
input#search-top-box-input,
input#search-top-box-input:hover,
div#login-status,
h1, #page-title,
blockquote:before,
div.blockquote:before,
table.wiki-content-table th,
.content-panel.centered.standalone.invite img,
.feature-block.customheight img {
        animation: neonanim 5s linear infinite both;
}
div#header {
    width: 100% !important;
    margin: 0;
    max-width: none !important;
    height: 400px;
    background-repeat: no-repeat;
    background-size: 100%;
}
body#html-body {
    background: #060a1d;
    overflow-x: hidden;
}
#page-content {
    color: white;
}
#extra-div-1 {
    position: absolute;
    top: 75px;
    height: 140px;
    left: 10px;
    transform: translateY(-50%);
    pointer-events: none;
    z-index: 10;
    width: 140px;
}

#extra-div-1 span {
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: url(https://scp-jp-storage.wdfiles.com/local--files/file%3A4053112-150-jg9r/AF2024.png);
    background-size: cover;
    filter: drop-shadow(0px 0px 6px #dd0d1d);
}

div#content-wrap,
div#main-content {
    position: static;
}

#header h1,
#header h2 {
    display: none;
}

.neon_titles {
    position: absolute;
    top: 75px;
    transform: translateY(-50%);
    z-index: 10;
    left: 160px;
    font-family: 'Nixie One', cursive;
    color: rgb(255 255 255 / 80%);
    line-height: 1;
}

@keyframes neonanim {
    0% {
        filter: hue-rotate(0deg)
    }
    100% {
        filter: hue-rotate(360deg)
    }
}

.maintitle {
    font-size: 33px;
    text-shadow: 0 0 3px #ff4747, 0 0 5px #ff4747, 0 0 6px #ff4747, 0 0 19px #ff4747, 0 0 10px #ff4747;
    padding-bottom: 5px;
    margin-bottom: 5px;
    position: relative;
}

.maintitle span {
    font-size: 45px;
}

.maintitle span span {
    animation: blink linear infinite 2s;
}

#extra-div-1 span {
    animation: blink linear infinite 3s;
}

.maintitle strong {
    font-weight: normal;
    animation: blink 1s linear infinite .6s;
}

.maintitle:before {
    bottom: 0;
    content: '';
    position: absolute;
    left: 0;
    height: 1px;
    width: 100%;
    background: rgb(255 255 255 / 90%);
    box-shadow: 0 0 3px #ff4747, 0 0 5px #ff4747, 0 0 6px #ff4747, 0 0 19px #ff4747, 0 0 10px #ff4747;
}

.subtitle {
    font-size: 18px;
    text-shadow: 0 0 3px #ff4747, 0 0 5px #ff4747, 0 0 6px #ff4747, 0 0 19px #ff4747, 0 0 10px #ff4747;
}

@keyframes blink {
    78% {
        opacity: 1
    }
    79% {
        opacity: 0
    }
    81% {
        opacity: 1
    }
    82% {
        opacity: 0
    }
    83% {
        opacity: 1
    }
    92% {
        opacity: 0
    }
    92.5% {
        opacity: 1
    }
}

div#top-bar,
.mobile-top-bar {
    max-width: none !important;
    left: 0;
}

div#top-bar {
    top: 400px;
    border: 2px solid rgb(255 255 255 / 3%);
    border-left: 0;
    border-right: 0;
}

div#top-bar:before,
div#top-bar:after {
    content: '';
    position: absolute;
    display: block;
    top: 0;
    left: 0;
    width: 100%;
    height: 2px;
    background: linear-gradient( 90deg, transparent, #da0a0a), linear-gradient( 90deg, transparent, #da0a0a);
    filter: drop-shadow(0 0 4px #da0a0a);
    animation: neonanim 5s linear infinite both, animate1 2s linear infinite;
    margin-top: -2px;
}

div#top-bar:after {
    top: unset;
    bottom: 0;
    animation-delay: .4s;
    margin-top: 0;
    margin-bottom: -2px;
}

@keyframes animate1 {
    0% {
        left: -100%;
    }
    50%,
    100% {
        left: 100%;
    }
}

#top-bar ul {
    float: right;
    display: flex;
    width: 100%;
}

#top-bar ul>li>ul {
    display: block;
}

#top-bar div {
    width: 100%;
    max-width: none;
}

#top-bar ul li {
    border: 0;
    position: relative;
    flex-grow: 1;
    flex-basis: 100%;
}

#top-bar li:hover ul,
#top-bar li.sfhover ul {
    width: 100%;
}

#top-bar ul li.sfhover ul li a,
#top-bar ul li:hover ul li a {
    width: 100%;
    box-sizing: border-box;
}

#top-bar ul li a[href="javascript:;"] {
    background: transparent !IMPORTANT;
    color: #9c9c9c !IMPORTANT;
    padding: 10px 1em;
    border: none;
}

@media (min-width: 767px) {
    #top-bar ul li a[href="/scp-international"] {
        background: transparent !IMPORTANT;
        color: #9c9c9c !IMPORTANT;
        padding: 10px 1em;
        border: none;
    }
}

#top-bar ul li.sfhover ul li a,
#top-bar ul li:hover ul li a {
    border-bottom: 2px solid #cc2b2b;
    color: #cc2b2b;
    background: #080808 !important;
    border-top: 0;
    padding: 5px 7px;
    font-family: 'Noto Sans JP', sans-serif;
}

#top-bar ul li ul {
    border: 0;
    box-shadow: none;
}

#top-bar ul li ul {
    box-sizing: border-box;
    border: 2px solid #cc2b2b;
    border-bottom: 0;
    border-top: none;
}

.mobile-top-bar {
    position: static;
}

#top-bar div>ul {
    position: absolute;
    top: 50%;
    transform: translateY(-50%);
    font-family: 'Noto Sans JP', sans-serif;
}

#top-bar ul li ul li:before,
#top-bar ul li ul li:after {
    content: '';
    position: absolute;
    left: 0;
    right: 0;
    height: 1px;
    background: #da0a0a;
    pointer-events: none;
}

#top-bar ul li ul li:hover:before {
    bottom: 100%;
    transition: .6s cubic-bezier(0.4, 0, 0.2, 1);
}

#top-bar ul li ul li:before {
    bottom: -2px;
}

#top-bar ul li ul li:after {
    top: -2px;
}

#top-bar ul li ul li:hover:after {
    top: 100%;
    transition: .6s ease-in-out;
}

#top-bar ul li ul li {
    overflow: hidden;
}

.subtitle span {
    animation: blink linear infinite 4s;
}

input.button.btn {
    display: none;
}

input#search-top-box-input,
input#search-top-box-input:hover {
    background-color: rgb(255 255 255 / 5%) !important;
    outline: none !important;
    border: 2px solid #cc2b2b !important;
    box-shadow: 0 0 5px #cc2b2b;
    border-radius: 0 0 5px 5px;
    padding: 0;
    width: 22px;
    height: 22px;
    background-image: url("data:image/svg+xml,%3Csvg%20xmlns%3D%22http%3A%2F%2Fwww.w3.org%2F2000%2Fsvg%22%20viewBox%3D%220%200%20512%20512%22%3E%3Cpath%20fill%3D%22%23cc2b2b%22%20d%3D%22M505%20442.7L405.3%20343c-4.5-4.5-10.6-7-17-7H372c27.6-35.3%2044-79.7%2044-128C416%2093.1%20322.9%200%20208%200S0%2093.1%200%20208s93.1%20208%20208%20208c48.3%200%2092.7-16.4%20128-44v16.3c0%206.4%202.5%2012.5%207%2017l99.7%2099.7c9.4%209.4%2024.6%209.4%2033.9%200l28.3-28.3c9.4-9.4%209.4-24.6.1-34zM208%20336c-70.7%200-128-57.2-128-128%200-70.7%2057.2-128%20128-128%2070.7%200%20128%2057.2%20128%20128%200%2070.7-57.2%20128-128%20128z%22%2F%3E%3C%2Fsvg%3E");
    background-size: 70%;
    background-position: center;
    background-repeat: no-repeat;
    color: transparent;
    transition: .5s cubic-bezier(0.68, -0.55, 0.27, 1.55), .3s ease-in-out box-shadow;
}

input#search-top-box-input:focus {
    background-image: none;
    width: 5em;
    color: #cc2b2b;
    box-shadow: 0 0 7px #cc2b2b;
    border-radius: 5px 0 5px 5px;
}

div#search-top-box {
    top: 29px;
    right: 4px;
}

a#account-topbutton {
    width: 22px;
    height: 22px;
    display: inline-flex;
    padding: 0;
    border: 2px solid #cc2b2b !important;
    box-shadow: 0 0 5px #cc2b2b;
    border-radius: 5px 5px 0 0;
    color: #cc2b2b;
    font-size: 110%;
    align-items: center;
    justify-content: center;
    transition: .3s ease-in-out;
    transform: translateY(3px);
}

div#login-status {
    top: 2px;
    right: 5px;
    font-family: 'Nixie One', cursive;
    font-weight: bold;
    color: #ca4a4a;
}

a#account-topbutton:hover {
    box-shadow: 0 0 9px #cc2b2b, 0 0 5px #cc2b2b;
}

input#search-top-box-input:hover {
    box-shadow: 0 0 9px #cc2b2b, 0 0 5px #cc2b2b;
}

span.printuser {
    color: rgb(255 255 255 / 90%);
    text-shadow: 0 0 2px #ff4747, 0 0 3px #ff4747;
}

a#my-account {
    animation: blink 2s linear infinite .3s;
    color: rgb(255 255 255 / 80%);
}

#login-status>a>strong {
    font-weight: normal;
    color: #fff;
}

input#search-top-box-input.empty {
    color: transparent;
}

div#account-options {
    background: #fff;
    border: 2px solid #cc2b2b;
}

div#account-options ul {
    display: flex;
    flex-wrap: wrap;
    justify-content: space-around;
    font-family: 'noto sans jp';
    font-weight: bold;
}

div#account-options li {
    flex-grow: 1;
    padding: 1px 2px;
    text-align: center;
    width: 50%;
    flex-basis: 50%;
    box-sizing: border-box;
    transition: .3s ease-in-out;
    color: #fff;
    text-shadow: 0 0 3px #cc2b2b, 0 0 4px #cc2b2b, 0 0 5px #cc2b2b;
}

div#account-options li a {
    color: inherit !important;
    text-decoration: none;
    text-shadow: inherit;
}

div#account-options li:hover {
    background: #ffc0c0;
    text-shadow: none;
    color: #cc2b2b;
}

@media (max-width:400px) {
div#extra-div-1 {
    left: -40px;
}
.neon_titles {
    left: 110px;
}
}

h1, #page-title {
    font-family: 'Nixie One','noto sans jp', cursive;
    line-height: 1;
    color: rgb(255 255 255 / 80%);
    text-shadow: 0 0 3px #ff4747, 0 0 6px #ff4747;
}

div#page-title {
    border-bottom: 1px solid #ffb2b2;
    text-align: center;
    letter-spacing: 2px;
}

blockquote, div.blockquote {
    background: rgb(255 255 255 / 5%);
    border: 2px solid #9c9c9c;
    position: relative;
}

blockquote:before,div.blockquote:before {
    content: '';
    position: absolute;
    left: 0;
    top: 0;
    bottom: 0;
    width: 3px;
    border-right: 2px solid #9c9c9c;
    background: #941414;
}

table.wiki-content-table {
    width: 100%;
}

table.wiki-content-table th {
    text-align: left;
    color: rgb(255 255 255 / 80%);
    background: #cc2b2b;
    font-family: 'noto sans jp';
    border-color: rgb(255 255 255 / 80%);
}
#side-bar {
    top: 420px;
}

#interwiki .side-block,
#side-bar .side-block {
    background: #060a1d !important;
    border: none;
    box-shadow: none;
    border-radius: 0;
    position: relative;
    overflow: hidden;
}

#interwiki .side-block:before,
#interwiki .side-block:after,
#interwiki .side-block>*:first-of-type:after,
#interwiki .side-block>*:first-of-type:before,
.side-block:before,
.side-block:after,
.side-block>*:first-of-type:after,
.side-block>*:first-of-type:before {
    content: '';
    display: block;
    position: absolute;
}

#interwiki .side-block:before,
.side-block:before {
    top: 0;
    left: 0;
    width: 100%;
    height: 2px;
    background: linear-gradient(90deg, transparent, #da0a0a);
    animation: animate1 1s linear infinite;
}

#interwiki .side-block:nth-of-type(even):before,
.side-block:nth-of-type(even):before {
    animation-delay: 0.25s;
}

#interwiki .side-block:after,
.side-block:after {
    top: -100%;
    right: 0;
    width: 2px;
    height: 100%;
    background: linear-gradient(180deg, transparent, #da0a0a);
    animation: animate2 1s linear infinite;
    animation-delay: 0.25s;
}

#interwiki .side-block:nth-of-type(even):after,
.side-block:nth-of-type(even):after {
    animation-delay: 0.50s;
}

#interwiki .side-block>*:first-of-type:before,
.side-block>*:first-of-type:before {
    bottom: 0;
    right: 0;
    width: 100%;
    height: 2px;
    background: linear-gradient(270deg, transparent, #da0a0a);
    animation: animate3 1s linear infinite;
    animation-delay: 0.50s;
}

#interwiki .side-block:nth-of-type(even)>*:first-of-type:before,
.side-block:nth-of-type(even)>*:first-of-type:before {
    animation-delay: 0.75s;
}

#interwiki .side-block>*:first-of-type:after,
.side-block>*:first-of-type:after {
    bottom: -100%;
    left: 0;
    width: 2px;
    height: 100%;
    background: linear-gradient(360deg, transparent, #da0a0a);
    animation: animate4 1s linear infinite;
    animation-delay: 0.75s;
}

#interwiki .side-block:nth-of-type(even)>*:first-of-type:after,
.side-block:nth-of-type(even)>*:first-of-type:after {
    animation-delay: 1s;
}

@keyframes animate4 {
    0% {
        bottom: -100%;
    }
    50%,
    100% {
        bottom: 100%;
    }
}

@keyframes animate3 {
    0% {
        right: -100%;
    }
    50%,
    100% {
        right: 100%;
    }
}

@keyframes animate2 {
    0% {
        top: -100%;
    }
    50%,
    100% {
        top: 100%;
    }
}

#interwiki #side-bar hr,
#side-bar hr {
    height: 1px;
    background: linear-gradient(to left, transparent,#ffffff42, transparent);
}

#interwiki #side-bar .menu-item > img,
#side-bar .menu-item > img {
     display: none;
}

#interwiki #side-bar .collapsible-block-folded,
#side-bar .collapsible-block-folded {
    background: none;
}

#interwiki #side-bar .collapsible-block-folded .collapsible-block-link,
#interwiki .menu-item > img + a:before,
#side-bar .collapsible-block-folded .collapsible-block-link:before,
#side-bar .menu-item > img + a:before {
    content: "\f02e";
    display: inline-block;
    font-family: 'FontAwesome';
    font-size: .9em;
    font-weight: 400;
    padding: 0 .25em 0 0;
    text-align: center;
    width: 1em;
}

#side-bar .collapsible-block-folded .collapsible-block-link:before {
    content: "\f067";
}
#side-bar .menu-item > img[src*="home.png"] + a:before {
    content: "\f015";
}
#side-bar .menu-item > img[src*="default.png"] + a:before {
    content: "\f041";
}
#side-bar .menu-item > img[src*="series.png"] + a:before {
