.scp-image-caption:after,
.scp-image-caption:before {
    content: '';
    display: block;
    position: absolute;
}

.scp-image-block:before {
    top: 0;
    left: 0;
    width: 100%;
    height: 2px;
    background: linear-gradient(90deg, transparent, #da0a0a);
    animation: animate1 1s linear infinite,neonanim 5s linear infinite both;
}

.scp-image-block:after {
    top: -100%;
    right: 0;
    width: 2px;
    height: 100%;
    background: linear-gradient(180deg, transparent, #da0a0a);
    animation: animate2 1s linear infinite,neonanim 5s linear infinite both;
    animation-delay: 0.25s;
}

.scp-image-caption:before {
    bottom: 0;
    right: 0;
    width: 100%;
    height: 2px;
    background: linear-gradient(270deg, transparent, #da0a0a);
    animation: animate3 1s linear infinite,neonanim 5s linear infinite both;
    animation-delay: 0.50s;
}

.scp-image-caption:after {
    bottom: -100%;
    left: 0;
    width: 2px;
    height: 100%;
    background: linear-gradient(360deg, transparent, #da0a0a);
    animation: animate4 1s linear infinite,neonanim 5s linear infinite both;
