[[module css]]
.c_international-container .panel-body {
    padding: 0;
}
.c_international-body {
    display: flex;
    justify-content: space-evenly;
    flex-wrap: wrap;
    *background: rgba(255,255,255,.9) url(http://www.scp-wiki.net/local--files/component:theme/panel-bg-gradient-reverse.png) bottom repeat-x;
    padding: 5px 0;
}
.c_international-box {
    margin: 5px 4px;
    text-align: center;
    min-width: 12em;
}
.c_international-box div > p,
.international-entry div > p {
    margin: 0;
}
.c_international-box .c_international-text {
    margin: 5px 0;
    font-size: 99%;
}
.international-entry {
    padding: 10px 10px 5px 10px;
    border-top: 1px solid #eeeee0;
}
.international-entry .international-description {
    margin: 5px 20px;
    font-size: 99%;
}
.c_international-box .c_international-logo img {
    height: 90px;
}


@media (min-width: 768px) and (max-width: 979px) {
    .c_international-box .c_international-logo img {
        height: 80px;
    }
    .c_international-box {
        min-width: 10em;
    }
}
@media (min-width: 581px) and (max-width: 767px) {
    .c_international-box .c_international-logo img {
        height: 70px;
    }
    .c_international-box {
        min-width: 10em;
    }
}
@media (min-width: 480px) and (max-width: 580px) {
    .c_international-box .c_international-logo img {
        height: 60px;
    }
    .c_international-box {
        min-width: 10em;
    }
}
@media (max-width: 479px) {
    .c_international-box .c_international-logo img {
        height: 50px;
    }
    .c_international-box {
        min-width: 10em;
    }
}
.content-panel {
    color: #333;
}
#page-content .content-panel:not(.standalone) {
    box-shadow: unset;
    background: url(https://scp-jp-storage.wdfiles.com/local--files/file%3A4053112-147-dwrt/da60d68a0760aaac.png);
    background-size: 800px;
    border-radius: 0px;
}
#page-content .content-panel .panel-heading {
    padding: 12px 10px;
}
#page-content .content-panel .panel-footer {
    background: #060a1d;
}

.news-block .panel-body {
    padding: 0;
}

.news-block .panel-body .alternate {
    padding: 5px 10px;
}

.news-block .news-title {
    font-weight: bold;
    font-size: 110%;
    margin-bottom: 5px;
    color: #633;
}

.news-block .news-content {
    margin: 5px 0;
}

.news-block .news-title > p, .news-block .news-content > p {
    margin: 0;
}

.news-block .list-pages-box .list-pages-item:nth-child(even) .alternate {
    background-color: rgba(255,255,255,.9);
}


