}

.news-block .news-title {
    font-weight: bold;
    font-size: 110%;
    margin-bottom: 5px;
    color: #633;
}

.news-block .news-content {
    margin: 5px 0;
}

.news-block .news-title > p, .news-block .news-content > p {
    margin: 0;
}

.news-block .list-pages-box .list-pages-item:nth-child(even) .alternate {
    background-color: rgba(255,255,255,.9);
}


