content:
[[iftags -구성요소-백엔드]]
[[module css]]

@import url('https://cdn.jsdelivr.net/font-nanum/1.0/nanumbarungothic/nanumbarungothic.css');
@import url('https://cdn.jsdelivr.net/gh/Nu-SCPTheme/Black-Highlighter@gh-pages/fonts/inter.css');

:root {
    --five-color: 196,2,51;
    --four-color: 255,109,0;
    --three-color: 255,211,0;
    --two-color: 0,135,189;
    --one-color: 0,159,107;
}

#page-title,
.meta-title {
    display: none;
}

.anom-bar-container,
.anom-bar-container * {
    -webkit-box-sizing: border-box;
       -moz-box-sizing: border-box;
     box-sizing: border-box;
    padding: 0;
    margin: 0;
    font-family: Inter, 'Nanum Barun Gothic', sans-serif;	
    font-family: var(--title-font, Inter, 'Nanum Barun Gothic', sans-serif);
}

.anom-bar-container {
    width: 100%;
    display: -webkit-box;
    display: -webkit-flex;
    display: -moz-box;
    display: -ms-flexbox;
    display: flex;
    position: relative;
}

.anom-bar {
    display: -ms-grid;
    display: grid;
     grid-template-areas:
     "top-box"
     "bottom-box";
    width: 100%;
}

.anom-bar > .top-box {
    --wght: 600;
    -ms-grid-row: 1;
    -ms-grid-column: 1;
    display: -ms-grid;
    display: grid;
    grid-area: top-box;
    grid-template-areas:
     "top-left-box top-center-box top-right-box";
    width: 100%;
     -ms-grid-columns: auto 1fr calc(5vw + 5rem);
    grid-template-columns: auto 1fr -webkit-calc(5vw + 5rem);
    grid-template-columns: auto 1fr -moz-calc(5vw + 5rem);
    grid-template-columns: auto 1fr calc(5vw + 5rem);
    -ms-grid-rows: 1fr;
    grid-template-rows: 1fr;
    font-weight: 600;
    text-transform: uppercase;
    justify-items: stretch;
    padding-bottom: 1rem;
}

.top-box .top-left-box {
    -ms-grid-row: 1;
    -ms-grid-column: 1;
    grid-area: top-left-box;
}

.top-left-box > .item {
    font-size: -webkit-calc(12px + (20 - 12) * ( (100vw - 300px) / ( 1920 - 960) ));
    font-size: -moz-calc(12px + (20 - 12) * ( (100vw - 300px) / ( 1920 - 960) ));
    font-size: calc(12px + (20 - 12) * ( (100vw - 300px) / ( 1920 - 960) ));
}

.top-left-box > .number {
    font-size: -webkit-calc(25px + (50 - 25) * ( (100vw - 300px) / ( 1920 - 300) ));
    font-size: -moz-calc(25px + (50 - 25) * ( (100vw - 300px) / ( 1920 - 300) ));
    font-size: calc(25px + (50 - 25) * ( (100vw - 300px) / ( 1920 - 300) ));
}

.top-box .top-center-box {
    -ms-grid-row: 1;
    -ms-grid-column: 2;
    display: -webkit-box;
    display: -webkit-flex;
    display: -moz-box;
    display: -ms-flexbox;
    display: flex;
    grid-area: top-center-box;
    -webkit-box-orient: vertical;
    -webkit-box-direction: normal;
    -webkit-flex-direction: column;
       -moz-box-orient: vertical;
       -moz-box-direction: normal;
     -ms-flex-direction: column;
     flex-direction: column;
    -webkit-box-align: center;
    -webkit-align-items: center;
       -moz-box-align: center;
     -ms-flex-align: center;
     align-items: center;
    -webkit-box-pack: center;
    -webkit-justify-content: center;
       -moz-box-pack: center;
     -ms-flex-pack: center;
     justify-content: center;
    height: 100%;
    margin: 0 1rem;
}

.top-center-box > div {
    background-color: rgb(0,0,0);
    height: 0.5rem;
    width: 100%;
    margin: 0.125rem;
    display: none;
}

.anom-bar-container.american .top-center-box > div {
    background-image:
    -webkit-repeating-linear-gradient(65deg,
     rgb(178,34,52),
     rgb(252,252,252) 45%,
     rgb(252,252,252) 55%,
     rgb(60,59,110) 100%);
    background-image:
    -moz-repeating-linear-gradient(65deg,
     rgb(178,34,52),
     rgb(252,252,252) 45%,
     rgb(252,252,252) 55%,
     rgb(60,59,110) 100%);
    background-image:
    -o-repeating-linear-gradient(65deg,
     rgb(178,34,52),
     rgb(252,252,252) 45%,
     rgb(252,252,252) 55%,
     rgb(60,59,110) 100%);
    background-image:
    repeating-linear-gradient(25deg,
     rgb(178,34,52),
     rgb(252,252,252) 45%,
     rgb(252,252,252) 55%,
     rgb(60,59,110) 100%);
    background-attachment: scroll;
}

.anom-bar-container.clear-1 .top-center-box > div {
    background-color: rgb(0,159,107);
    background-color: rgb(var(--one-color));
}

.anom-bar-container.clear-2 .top-center-box > div {
    background-color: rgb(0,135,189);
    background-color: rgb(var(--two-color));
}

.anom-bar-container.clear-3 .top-center-box > div {
    background-color: rgb(255,211,0);
    background-color: rgb(var(--three-color));
}

.anom-bar-container.clear-4 .top-center-box > div {
    background-color: rgb(255,109,0);
    background-color: rgb(var(--four-color));
}

.anom-bar-container.clear-5 .top-center-box > div {
    background-color: rgb(196,2,51);
    background-color: rgb(var(--five-color));
}

.anom-bar-container.clear-1 .top-center-box > .bar-one,
.anom-bar-container.clear-2 .top-center-box > .bar-one,
.anom-bar-container.clear-3 .top-center-box > .bar-one,
.anom-bar-container.clear-4 .top-center-box > .bar-one,
.anom-bar-container.clear-5 .top-center-box > .bar-one,
.anom-bar-container.clear-6 .top-center-box > .bar-one {
    display: block;
}

.anom-bar-container.clear-2 .top-center-box > .bar-two,
.anom-bar-container.clear-3 .top-center-box > .bar-two,
.anom-bar-container.clear-4 .top-center-box > .bar-two,
.anom-bar-container.clear-5 .top-center-box > .bar-two,
.anom-bar-container.clear-6 .top-center-box > .bar-two {
    display: block;
}

.anom-bar-container.clear-3 .top-center-box > .bar-three,
.anom-bar-container.clear-4 .top-center-box > .bar-three,
.anom-bar-container.clear-5 .top-center-box > .bar-three,
.anom-bar-container.clear-6 .top-center-box > .bar-three {
    display: block;
}

.anom-bar-container.clear-4 .top-center-box > .bar-four,
.anom-bar-container.clear-5 .top-center-box > .bar-four,
.anom-bar-container.clear-6 .top-center-box > .bar-four {
    display: block;
}

.anom-bar-container.clear-5 .top-center-box > .bar-five,
.anom-bar-container.clear-6 .top-center-box > .bar-five {
    display: block;
}

.anom-bar-container.clear-6 .top-center-box > .bar-six {
    display: block;
}

.anom-bar-container.clear-6 .top-center-box > div {
    background-color: rgb(133, 0, 5);
    background-color: rgb(var(--swatch-primary, 133, 0, 5));
    background-image: url("https://scp-wiki.wdfiles.com/local--files/component%3Aanomaly-class-bar/galactic.jpg");
    background-attachment: fixed;
    background-size: contain;
    background-blend-mode: overlay;
}

.top-box .top-right-box {
    -ms-grid-row: 1;
    -ms-grid-column: 3;
    display: -ms-grid;
    display: grid;
    grid-area: top-right-box;
     grid-template-areas:
     "level"
     "clearance";
    -ms-grid-rows: (1fr)[2];
    grid-template-rows: repeat(2, 1fr);
    grid-gap: 0.5em;	
    gap: 0.5em;
}

.top-right-box > .level {
    -ms-grid-row: 1;
    -ms-grid-column: 1;
    display: -webkit-box;
    display: -webkit-flex;
    display: -moz-box;
    display: -ms-flexbox;
    display: flex;
    -webkit-box-align: end;
    -webkit-align-items: flex-end;
       -moz-box-align: end;
     -ms-flex-align: end;
     align-items: flex-end;
    grid-area: level;
    font-size: 2.5em;
    line-height: 0.5;
}

.top-right-box > .clearance {
    -ms-grid-row: 2;
    -ms-grid-column: 1;
    display: -webkit-box;
    display: -webkit-flex;
    display: -moz-box;
    display: -ms-flexbox;
    display: flex;
    -webkit-box-align: start;
    -webkit-align-items: flex-start;
       -moz-box-align: start;
     -ms-flex-align: start;
     align-items: flex-start;
    position: relative;
    width: 100%;
    height: 100%;
    grid-area: clearance;
    font-size: -webkit-calc(12px + (15 - 12) * ( (100vw - 400px) / ( 1920 - 960) ));
    font-size: -moz-calc(12px + (15 - 12) * ( (100vw - 400px) / ( 1920 - 960) ));
    font-size: calc(12px + (15 - 12) * ( (100vw - 400px) / ( 1920 - 960) ));
    line-height: 1.5;
    white-space: nowrap;
}

.top-right-box > .clearance::before {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
}

.anom-bar-container.clear-6 .top-right-box > .clearance::before {
    content: "우주극비";
}

.anom-bar-container.clear-5 .top-right-box > .clearance::before {
    content: "극비";
}

.anom-bar-container.clear-4 .top-right-box > .clearance::before {
    content: "비밀";
}

.anom-bar-container.clear-3 .top-right-box > .clearance::before {
    content: "신임";
}

.anom-bar-container.clear-2 .top-right-box > .clearance::before {
    content: "제한";
}

.anom-bar-container.clear-1 .top-right-box > .clearance::before {
    content: "무제한";
}

.anom-bar > .bottom-box {
    --wght: 600;
    -ms-grid-row: 2;
    -ms-grid-column: 1;
    display: -ms-grid;
    display: grid;
    grid-area: bottom-box;
     grid-template-areas:
     "text-part diamond-part"
     "text-part diamond-part";
    -ms-grid-rows: 1fr 0.5rem 1fr;
    grid-template-rows: 1fr 1fr;
    -ms-grid-columns: 1fr 0.5rem 6rem;
    grid-template-columns: 1fr 6rem;
    grid-gap: 0.5rem;
    width: 100%;
    height: 6rem;
    font-weight: 600;
    -webkit-box-shadow: 0 -0.5rem 0 0 rgb(12, 12, 12);
       -moz-box-shadow: 0 -0.5rem 0 0 rgb(12, 12, 12);
     box-shadow: 0 -0.5rem 0 0 rgb(12, 12, 12);
    -webkit-box-shadow: 0 -0.5rem 0 0 rgb(var(--swatch-menubg-black-color, 12, 12, 12));
       -moz-box-shadow: 0 -0.5rem 0 0 rgb(var(--swatch-menubg-black-color, 12, 12, 12));
     box-shadow: 0 -0.5rem 0 0 rgb(var(--swatch-menubg-black-color, 12, 12, 12));
    margin-top: -0.5rem;
    padding-top: 0.25rem;
}

.bottom-box > .text-part {
    -ms-grid-row: 1;
    -ms-grid-row-span: 3;
    -ms-grid-column: 1;
    display: -ms-grid;
    display: grid;
    height: 6rem;
    grid-area: text-part;
     grid-template-areas:
     "main-class disrupt-class"
     "main-class risk-class";
    -ms-grid-rows: 1fr 0.25rem 1fr;
    grid-template-rows: 1fr 1fr;
    -ms-grid-columns: auto 0.25rem 1fr;
    grid-template-columns: 3fr 2fr;
    grid-gap: 0.25rem;
    text-transform: uppercase;
    -webkit-box-pack: stretch;
    -webkit-justify-content: stretch;
       -moz-box-pack: stretch;
     -ms-flex-pack: stretch;
     justify-content: stretch;
    -webkit-box-align: stretch;
    -webkit-align-items: stretch;
       -moz-box-align: stretch;
     -ms-flex-align: stretch;
     align-items: stretch;
}

.text-part div {
    padding: 0 0.125rem;
    margin: 0 0 0 0.25rem;
    position: relative;
}

.text-part > .main-class {
    -ms-grid-row: 1;
    -ms-grid-row-span: 3;
    -ms-grid-column: 1;
    position: relative;
    grid-area: main-class;
    display: -webkit-box;
    display: -webkit-flex;
    display: -moz-box;
    display: -ms-flexbox;
    display: flex;
    -webkit-box-orient: vertical;
    -webkit-box-direction: normal;
    -webkit-flex-direction: column;
       -moz-box-orient: vertical;
       -moz-box-direction: normal;
     -ms-flex-direction: column;
     flex-direction: column;
    -webkit-box-align: center;
    -webkit-align-items: center;
       -moz-box-align: center;
     -ms-flex-align: center;
     align-items: center;
    -webkit-box-pack: center;
    -webkit-justify-content: center;
       -moz-box-pack: center;
     -ms-flex-pack: center;
     justify-content: center;
    -webkit-box-shadow: 0.25rem 0 0 0 rgba(0,0,0,0);
       -moz-box-shadow: 0.25rem 0 0 0 rgba(0,0,0,0);
     box-shadow: 0.25rem 0 0 0 rgba(0,0,0,0);
}

.anom-bar-container:not(.난해) .text-part > .main-class {
    padding-right: 3.5rem;
}

.anom-bar-container.난해 .text-part > .main-class {
    padding-right: 2rem;
}

.text-part > .main-class::before {
    content: "";
    position: absolute;
    top: -webkit-calc(50% - 2.25rem);
    top: -moz-calc(50% - 2.25rem);
    top: calc(50% - 2.25rem);
    right: 0.5rem;
    width: 3.53125rem;
    height: 3.53125rem;
    -webkit-border-radius: 50%;
       -moz-border-radius: 50%;
            border-radius: 50%;
    border: 0.25rem solid rgb(12, 12, 12);
border: 0.25rem solid rgb(var(--swatch-menutxt-dark-color, 12, 12, 12));
    background-color: rgb(252, 252, 252);
background-color: rgb(var(--swatch-background, 252, 252, 252));
    -webkit-background-size: contain;
       -moz-background-size: contain;
         -o-background-size: contain;
            background-size: contain;
    background-position: center center;
}

.anom-bar-container.난해 .text-part > .main-class::before {
    top: 0.5rem;
    right: 1.25rem;
    width: 5rem;
    -webkit-border-radius: -webkit-calc(3.53125rem / 2);
       -moz-border-radius: -moz-calc(3.53125rem / 2);
            border-radius: calc(3.53125rem / 2);
    background-color: rgb(252, 252, 252);
background-color: rgb(var(--swatch-background, 252, 252, 252));
    border-color: rgb(var(--swatch-background, 252, 252, 252));
    -webkit-filter: invert(1);
            filter: invert(1);
    background-image: url("https://scp-wiki.wikidot.com/local--files/component:anomaly-class-bar/esoteric-icon.svg");
    -webkit-background-size: 75% 75%;
       -moz-background-size: 75% 75%;
         -o-background-size: 75% 75%;
            background-size: 75% 75%;
    background-repeat: no-repeat;
    background-position: left -10px center;
}

.anom-bar-container.난해 .text-part > .main-class::after {
    content: "";
    position: absolute;
    top: 0.5rem;
    right: 0.25rem;
    width: 3.53125rem;
    height: 3.53125rem;
    -webkit-border-radius: 50%;
       -moz-border-radius: 50%;
            border-radius: 50%;
    border: 0.25rem solid rgb(12, 12, 12);
border: 0.25rem solid rgb(var(--swatch-menutxt-dark-color, 12, 12, 12));
    background-color: rgb(252, 252, 252);
background-color: rgb(var(--swatch-background, 252, 252, 252));
    -webkit-background-size: contain;
       -moz-background-size: contain;
         -o-background-size: contain;
            background-size: contain;
    background-position: center center;
}

.anom-bar-container.{$secondary-class} .text-part > .main-class::after {
    background-image: url("{$secondary-icon}");
}

.anom-bar-container.해명 .text-part > .main-class::before,
.anom-bar-container.보류 .text-part > .main-class::before,
.anom-bar-container.무효 .text-part > .main-class::before {
    display:none;
}

.anom-bar-container.케테르 .text-part > .main-class::before {
    background-color: rgb(196,2,51);
background-color: rgb(var(--five-color));
    background-image: url("https://scp-wiki.wikidot.com/local--files/component:anomaly-class-bar/keter-icon.svg");
}

.anom-bar-container.유클리드 .text-part > .main-class::before {
    background-color: rgb(255,211,0);
background-color: rgb(var(--three-color));
    background-image: url("https://scp-wiki.wikidot.com/local--files/component:anomaly-class-bar/euclid-icon.svg");
}

.anom-bar-container.안전 .text-part > .main-class::before {
    background-color: rgb(0,159,107);
background-color: rgb(var(--one-color));
    background-image: url("https://scp-wiki.wikidot.com/local--files/component:anomaly-class-bar/safe-icon.svg");
}

.text-part > .main-class > div {
    width: 100%;
    display: -webkit-box;
    display: -webkit-flex;
    display: -moz-box;
    display: -ms-flexbox;
    display: flex;
}

.anom-bar-container.난해 .text-part > .main-class > .second-class > .class-text {
    font-size: -webkit-calc( 6px + (11 - 6) * ( (100vw - 400px) / ( 800 - 400) ));
    font-size: -moz-calc( 6px + (11 - 6) * ( (100vw - 400px) / ( 800 - 400) ));
    font-size: calc( 6px + (11 - 6) * ( (100vw - 400px) / ( 800 - 400) ));
    position: relative;
}

.anom-bar-container:not(.난해) .text-part > .main-class > .contain-class > .class-text {
    font-size: -webkit-calc(10px + (18 - 10) * ( (100vw - 400px) / ( 800 - 400) ));
    font-size: -moz-calc(10px + (18 - 10) * ( (100vw - 400px) / ( 800 - 400) ));
    font-size: calc(10px + (18 - 10) * ( (100vw - 400px) / ( 800 - 400) ));
}

.anom-bar-container:not(.난해) .text-part > .main-class > .second-class {
    display: none;
}

.text-part > .disrupt-class {
    -ms-grid-row: 1;
    -ms-grid-column: 3;
    grid-area: disrupt-class;
}

.text-part > .risk-class {
    -ms-grid-row: 3;
    -ms-grid-column: 3;
    grid-area: risk-class;
}

.text-part > .disrupt-class,
.text-part > .risk-class {
    overflow: hidden;
}

.anom-bar-container.난해 .text-part .main-class {
    background-color: rgba(66, 66, 72 ,0.15);
    background-color: rgba(var(--swatch-menubg-dark-color, 66, 66, 72),0.15);
    border-left: 0.75rem solid rgba(66, 66, 72 ,1);
    border-left: 0.75rem solid rgba(var(--swatch-menubg-dark-color, 66, 66, 72),1);
}

.anom-bar-container.무효 .text-part .main-class {
    background-color: rgba(66, 66, 72 ,0.15);
    background-color: rgba(var(--swatch-menubg-dark-color, 66, 66, 72),0.15);
    border-left: 0.75rem solid rgba(66, 66, 72 ,1);
    border-left: 0.75rem solid rgba(var(--swatch-menubg-dark-color, 66, 66, 72),1);
}

.anom-bar-container.보류 .text-part .main-class {
    background-color: rgba(12, 12, 12 ,0.15);
    background-color: rgba(var(--swatch-menutxt-dark-color, 12, 12, 12),0.15);
    border-left: 0.75rem solid rgba(12, 12, 12 ,1);
    border-left: 0.75rem solid rgba(var(--swatch-menutxt-dark-color, 12, 12, 12),1);
}

.anom-bar-container.해명 .text-part .main-class {
    background-color: rgba(252, 252, 252 ,0.15);
    background-color: rgba(var(--swatch-background, 252, 252, 252),0.15);
    border-left: 0.75rem solid rgba(12, 12, 12 ,1);
    border-left: 0.75rem solid rgba(var(--swatch-menutxt-dark-color, 12, 12, 12),1);
}

.anom-bar-container.케테르 .text-part .main-class,
.anom-bar-container.아미타 .text-part .disrupt-class,
.anom-bar-container.치명 .text-part .risk-class {
    background-color: rgba(196,2,51,0.15);
    background-color: rgba(var(--five-color),0.15);
    border-left: 0.5rem solid rgba(196,2,51,1);
    border-left: 0.5rem solid rgba(var(--five-color),1);
}

.anom-bar-container.에키 .text-part .disrupt-class,
.anom-bar-container.위해 .text-part .risk-class {
    background-color: rgba(255,109,0,0.15);
    background-color: rgba(var(--four-color),0.15);
    border-left: 0.5rem solid rgba(255,109,0,1);
    border-left: 0.5rem solid rgba(var(--four-color),1);
}

.anom-bar-container.유클리드 .text-part .main-class,
.anom-bar-container.커너크 .text-part .disrupt-class,
.anom-bar-container.경고 .text-part .risk-class {
    border-left: 0.5rem solid rgba(255,211,0,1);
    border-left: 0.5rem solid rgba(var(--three-color),1);
    background-color: rgba(255,211,0,0.15);
    background-color: rgba(var(--three-color),0.15);
}

.anom-bar-container.플람 .text-part .disrupt-class,
.anom-bar-container.주의 .text-part .risk-class {
    border-left: 0.5rem solid rgba(0,135,189,1);
    border-left: 0.5rem solid rgba(var(--two-color),1);
    background-color: rgba(0,135,189,0.15);
    background-color: rgba(var(--two-color),0.15);
}

.anom-bar-container.안전 .text-part .main-class,
.anom-bar-container.암흑 .text-part .disrupt-class,
.anom-bar-container.주목 .text-part .risk-class {
    background-color: rgba(0,159,107,0.15);
    background-color: rgba(var(--one-color),0.15);
    border-left: 0.5rem solid rgba(0,159,107,1);
    border-left: 0.5rem solid rgba(var(--one-color),1);
}

