[[iftags [[#ifexpr {$self-defining} == (false) | - ]]]]


[[module ListPages category="{$module-category}"  order="{$module-order}" separate="yes"  perPage="{$module-perpage}"  limit="{$module-limit}" tags="{$module-tags}" created_by="{$module-created-by}" ]]

[[div id="listbook-{$list-book}" class="dust-jacket cover-{$book-cover}  "]]
[[div class="book-list link-{$link-color} " ]]
{$module-link}
[[/div]]
[[/div]]

[[div class="book-description book-annotation-1 "]]
{$book-info}
[[/div]]

[[div class="float-info info-1  book-annotation-2"]]
[[div class="info-under"]]
{$book-info}
[[/div]]
[[/div]]


[[div class="module-shelf-block" style=""]]
[[/div]]


[[/module]]


[[/iftags]]

[!--此处应放自定义组件--]

[[iftags [[#ifexpr {$self-defining} == (true) | - ]]]]


[[module ListPages category="{$module-category}"  order="{$module-order}" separate="yes"  perPage="{$module-perpage}"  limit="{$module-limit}" tags="{$module-tags}" created_by="{$module-created-by}" ]]


[[div id="listbook-{$list-book}" class="self-defining"]]
[[div class="dust-jacket cover-{$book-cover}  cover-{$self-defining}"]]
[[div class="book-list link-{$link-color} link-color" ]]
{$module-link}
[[/div]]
[[/div]]

[[div class="book-description description-{$self-defining} book-annotation-1 "]]
{$book-info}
[[/div]]

[[div class="float-info info-1 info-{$self-defining} book-annotation-2"]]
[[div class="info-under"]]
{$book-info}
[[/div]]
[[/div]]
[[/div]]

[[div class="module-shelf-block" style=""]]
[[/div]]

[[/module]]


[[module css]]

:root{
--box-true-{$list-book}:0.0625rem 0.0625rem 0 {$self-book-color}, 0.125rem 0.125rem 0 {$self-book-color}, 0.1875rem 0.1875rem 0 {$self-book-color}, 0.25rem 0.25rem 0 {$self-book-color}, 0.3125rem 0.3125rem 0 {$self-book-color}, 0.375rem 0.375rem 0 {$self-book-color}, 0.4375rem 0.4375rem 0 {$self-book-color}, 0.58rem 0.8rem 0 {$self-book-color};
}




#u-listbook-{$list-book} .cover-true  {
    background:{$self-book-background};
    border-left:{$self-book-border};
    border-right:{$self-book-border};

    -moz-box-shadow: var(--box-true-{$list-book});
    -o-box-shadow: var(--box-true-{$list-book});
    -webkit-box-shadow: var(--box-true-{$list-book});
    box-shadow: var(--box-true-{$list-book});
}


@media (max-width: 479px) {
.trans-yes  #u-listbook-{$list-book} .cover-true {
    border-left:0;
    border-right:0;
    border-top:{$self-book-border} ;
    border-bottom:{$self-book-border}  ;
}
}

#u-listbook-{$list-book} .link-true a {
    color:{$self-link-color};
}

#u-listbook-{$list-book} .description-true {
    color:{$self-description-color};
}

#u-listbook-{$list-book} .info-true  {
 padding:1%;
 box-shadow:{$self-info-shadow};
background:{$self-info-background};
border: {$self-info-border};
}


[[/module]]
[[/iftags]]
