content:
[[module css]]
.book-annotation-1,
.book-annotation-2 {
display:none;
}

.shelf-{$list-shelf} .book-annotation-{$book-annotation} {
display:block !important;
}

.my-plank-{$my-plank} .et-plank {
    background: {$plank-background};
    -webkit-filter:  {$plank-filter};
    filter: {$plank-filter};
    transform: rotateX(0deg);
    background-blend-mode: overlay, normal;
    background-size:  {$plank- background-size};
    border-radius:{$plank-border};
    border-radius:{$plank-border-radius};
}


[[/module]]


[[div class="shelf-{$list-shelf} shelf-wrap trans-{$trans}  my-plank-{$my-plank}"]]
[[div class="shelf-{$trans}"]]
