content:
[[module css]]

/*
=====================
被放逐者的书架
由Etinjat制作
书籍边框和书籍底图来自woedenaz
=====================
*/

:root{   
    /* 图片赋值 */

    --gold-image: url("http://scpsandboxcn.wikidot.com/local--files/image/IMG_golden-sheet.png");

    --wave-image: url("http://scpsandboxcn.wikidot.com/local--files/image/Bgr-deco-pattern.png");

    --cloud-image: url("http://scpsandboxcn.wikidot.com/local--files/image/Bgr-white-waves.png");

    --night-image: url("http://scpsandboxcn.wikidot.com/local--files/image/Bgr-galfed.png");

    --bluepaper-image: url("http://scpsandboxcn.wikidot.com/local--files/image/Bgr-WL-Blue.png");

 /* 投影赋值 */   

   --box-out-shelf:0.0625rem -0.0625rem 0 #4c2e13, 0.125rem -0.125rem 0 #4c2e13, 0.1875rem -0.1875rem 0 #4c2e13, 0.25rem -0.25rem 0 #4c2e13, 0.3125rem -0.3125rem 0 #4c2e13, 0.375rem -0.375rem 0 #4c2e13, 0.4375rem -0.4375rem 0 #4c2e13, 0.8rem -0.8rem 0 #4c2e13;

   --box-inner-shelf:-0.0625rem -0.0625rem 0 #4c2e13, -0.125rem -0.125rem 0 #4c2e13, -0.1875rem -0.1875rem 0 #4c2e13, -0.25rem -0.25rem 0 #4c2e13, -0.3125rem -0.3125rem 0 #4c2e13, -0.375rem -0.375rem 0 #4c2e13, -0.4375rem -0.4375rem 0 #4c2e13, -0.5rem -0.5rem 0 #4c2e13;

   --box-green:0.0625rem 0.0625rem 0 #385814, 0.125rem 0.125rem 0 #385b15, 0.1875rem 0.1875rem 0 #3a5e16, 0.25rem 0.25rem 0 #3b6118, 0.3125rem 0.3125rem 0 #40641a, 0.375rem 0.375rem 0 #385814, 0.4375rem 0.4375rem 0 #3d5c15, 0.58rem 0.8rem 0 #31540e;

   --box-white:0.0625rem 0.0625rem 0 #94a19d, 0.125rem 0.125rem 0 #acb0af, 0.1875rem 0.1875rem 0 #b6aaaa, 0.25rem 0.25rem 0 #a5a9a0, 0.3125rem 0.3125rem 0 #848881, 0.375rem 0.375rem 0 #81847f, 0.4375rem 0.4375rem 0 #545552, 0.58rem 0.8rem 0 #4e504d;

   --box-black:0.0625rem 0.0625rem 0 #54584f, 0.125rem 0.125rem 0 #484c45, 0.1875rem 0.1875rem 0 #363a32, 0.25rem 0.25rem 0 #3a4133, 0.3125rem 0.3125rem 0 #40433c, 0.375rem 0.375rem 0 #32342f, 0.4375rem 0.4375rem 0 #1e1e1d, 0.58rem 0.8rem 0 #121212;

   --box-red:0.0625rem 0.0625rem 0 #921212, 0.125rem 0.125rem 0 #901111, 0.1875rem 0.1875rem 0 #a92d0f, 0.25rem 0.25rem 0 #870e21, 0.3125rem 0.3125rem 0 #74060d, 0.375rem 0.375rem 0 #6b1808, 0.4375rem 0.4375rem 0 #5b0b0f, 0.58rem 0.8rem 0 #3c040f;

   --box-gold:0.0625rem 0.0625rem 0 #aead42, 0.125rem 0.125rem 0 #a07512, 0.1875rem 0.1875rem 0 #b2781c, 0.25rem 0.25rem 0 #a37716, 0.3125rem 0.3125rem 0 #71560d, 0.375rem 0.375rem 0 #6e6615, 0.4375rem 0.4375rem 0 #827519, 0.58rem 0.8rem 0 #4e4208;

   --box-blue:0.0625rem 0.0625rem 0 #12aab4, 0.125rem 0.125rem 0 #1f98d0, 0.1875rem 0.1875rem 0 #17898f, 0.25rem 0.25rem 0 #136f98, 0.3125rem 0.3125rem 0 #1d4b8d, 0.375rem 0.375rem 0 #144890, 0.4375rem 0.4375rem 0 #0d386b, 0.58rem 0.8rem 0 #083241;

  --box-darkblue:0.0625rem 0.0625rem 0 #0c1481, 0.125rem 0.125rem 0 #1e0c8c, 0.1875rem 0.1875rem 0 #0a1172, 0.25rem 0.25rem 0 #0d1683, 0.3125rem 0.3125rem 0 #0a1160, 0.375rem 0.375rem 0 #0c1474, 0.4375rem 0.4375rem 0 #0b104e, 0.58rem 0.8rem 0 #0c114f;

 --box-bluedark:0.0625rem 0.0625rem 0 #0d3e40, 0.125rem 0.125rem 0 #20364f, 0.1875rem 0.1875rem 0 #162c44, 0.25rem 0.25rem 0 #162c44, 0.3125rem 0.3125rem 0 #0a2244, 0.375rem 0.375rem 0 #061d3c, 0.4375rem 0.4375rem 0 #04162b, 0.58rem 0.8rem 0 #081c24;


  --box-galaxy:0.0625rem 0.0625rem 0 #9b01d3, 0.125rem 0.125rem 0 #1c0463, 0.1875rem 0.1875rem 0 #3c0875, 0.25rem 0.25rem 0 #2e0d65, 0.3125rem 0.3125rem 0 #3d0a5d, 0.375rem 0.375rem 0 #340438, 0.4375rem 0.4375rem 0 #1a041c, 0.58rem 0.8rem 0 #1a041a;

  --box-purple:0.0625rem 0.0625rem 0 #f303d6, 0.125rem 0.125rem 0 #9d11a3, 0.1875rem 0.1875rem 0 #881391, 0.25rem 0.25rem 0 #650e6a, 0.3125rem 0.3125rem 0 #490c49, 0.375rem 0.375rem 0 #30032f, 0.4375rem 0.4375rem 0 #2f032b, 0.58rem 0.8rem 0 #1f0226;

  --box-mix-1:0.0625rem 0.0625rem 0 #142a49, 0.125rem 0.125rem 0 #1f301b, 0.1875rem 0.1875rem 0 #173240, 0.25rem 0.25rem 0 #1f4127, 0.3125rem 0.3125rem 0 #080840, 0.375rem 0.375rem 0 #1f0a4a, 0.4375rem 0.4375rem 0 #180840, 0.58rem 0.8rem 0 #0f1b02;

--book-height:15rem;

}

/*
=====================
书架
=====================
*/

.et-out-bookrack{
    padding: 1rem 0 0.5rem 1.5rem;
    filter: drop-shadow(1.5rem 0.2rem 3px #422306);
    background: radial-gradient(#b16d1d, #f3cfa5, #684015);
    background: -moz-radial-gradient(#b16d1d, #f3cfa5, #684015);
    background: -webkit-radial-gradient(#b16d1d, #f3cfa5, #684015);
/*
    background-image:  var(--gold-image),
                              linear-gradient(rgb(117, 89, 6) 0%, rgb(84, 59, 16) 30%, rgb(117, 61, 12) 100%);
    background-blend-mode: overlay, normal;
    background-size: cover, 100% 260%;
*/
    -moz-box-shadow: var(--box-out-shelf);
    -o-box-shadow: var(--box-out-shelf);
    -webkit-box-shadow: var(--box-out-shelf);
    box-shadow: var(--box-out-shelf);
}

.et-inner-bookrack{
    background: linear-gradient(43deg, #f9c29c,#cbcbcb,#d3c895);
    padding: 2rem 0rem 0rem 0rem;
    margin: 0.5rem 1.5rem 1rem 0rem;
    filter: drop-shadow(1px 1px 10px #422306);
    height: auto;
  /* box-shadow: var(--box-inner-shelf); */
    border: 0.8rem inset #866139;

    border-right: 0rem inset #866139;

}

/*
=====================
书格
=====================
*/

.shelf-no , .shelf-yes ,
.shelf-no .list-pages-box,
.shelf-yes  .list-pages-box{
    display: flex;
    min-height: 15rem;
    overflow-y:hidden; 
    margin: 0 0rem 0 1rem;
    padding:3rem 0 0 0;
}

.shelf-no .list-pages-box{
     overflow:visible;
     padding:0.1rem 0 1rem 0;
}

/* 手机间隔 */
@media (max-width: 479px) {
  .et-out-bookrack{
    padding:1rem 0.6rem 1rem 0.7rem ;
 }

  .et-inner-bookrack{
     padding: 0.5rem 0.1rem 0rem 0.1rem ;
     margin: 0.2rem 0rem 0rem 0rem ;
  }

  .shelf-no {
     margin: 0rem 0.1rem 0 0.1rem;
     padding:4rem 0 0 0;
  }

  .shelf-no   .list-pages-box{
     margin: 0rem 0.1rem 0 0.1rem;
     padding:0rem 0 0 0;
  }
}

/*
=====================
封面
=====================
*/

/*
--------------------------
书封
--------------------------
*/

.dust-jacket{
    margin: 0 0 0 0rem;
 /* padding: 0.5rem 1rem 0 1rem;*/
    width: 3rem;

    display: inline-block;
    transition: all 1s;
    filter:brightness(1) drop-shadow(13px 5px 1px #333);
    -webkit-filter:brightness(1) drop-shadow(13px 5px 1px #333);
    border-radius: 0.2rem;
}

@media only screen and (min-width: 600px) {
    /* 取书 */
  .dust-jacket:hover{
    -webkit-filter: brightness(2) drop-shadow(6px 20px 1px #000);
    filter: brightness(2) drop-shadow(6px 20px 1px #333);   /* 如果把最后的1改成0，可能会有聚焦效果，但可能也更卡*/
    transform:rotateX(20deg);
   -webkit-transform:rotateX(-25deg); /* Safari and Chrome */
    -moz-transform:rotateX(-25deg); /* Firefox */
    transition: all 1s;

    margin-bottom:2rem;
    margin-top:-2rem;

/* 似乎不起作用
perspective: 1410;
perspective-origin: 10% 10%;
-webkit-perspective: 1410;
-webkit-perspective-origin: 10% 10%;
-moz-perspective: 1410;
-moz-perspective-origin: 10% 10%;
*/
  }
}

/*
@media (max-width: 479px) {
  .dust-jacket:hover{
  margin-bottom:2rem;
   margin-top:-2rem;
  }
}
*/

/* 被选中时不会有选择带 */
.dust-jacket::selection {
    background:transparent;
}

.dust-jacket ::-moz-selection  {
    background:transparent;
}

/*
--------------------------
书皮
--------------------------
*/

.book-list {
   display: inline-block;
   min-width:3rem;
  /*  margin: 1em 1em 0 1em;*/
 /*  line-height:28px;*/
   float:left;
}

.book-list a{
    margin: 0 0 0 0.9rem;
    max-height: calc(var(--book-height) - 2rem);

    overflow: hidden;
    text-overflow:ellipsis;
    text-overflow:'◈';

    white-space: nowrap;

    writing-mode: vertical-lr;
    -webkit-writing-mode: vertical-lr;
    -ms-writing-mode: vertical-lr;

    text-decoration:none;
    font-size: 135%;
    font-family: kaiti,'华文新魏';
   /* padding: 0 1px 0  1px;*/
}

.book-list a{
    min-height: 13.5rem;

}

.book-list a:hover{
    background-color: transparent;
}

/*
似乎无效
.book-list {
   display: inline-block;
   margin: 1em 1em 0 1em;
   min-width:0.5em;
   line-height:30px;
   float:left;
   height:auto;
}

.book-list a{
    text-decoration:none;
    font-family: '华文楷体',kaiti,'华文新魏';
    font-size: 135%;

    margin: 0 1px 0 1px !important;
   height: 13em;

    writing-mode: vertical-lr;
  -webkit-writing-mode: vertical-lr;
  -ms-writing-mode: vertical-lr;

}
*/

/*
=====================
书籍间隔
=====================
*/
.the-shelf-block{
   width:10px;
}

.module-shelf-block{
   width:5px;
   display: inline-flex;
}

/*废弃
@media (max-width: 479px) {
.the-shelf-block:nth-child(3)
{
width:100%;
height:1em;
background:grey;
}
}
*/

/*
=====================
隔板
=====================
*/

.et-plank{
background-image: var(--gold-image), linear-gradient(rgb(134, 103, 34) 0%, rgb(88, 56, 5) 30%, rgb(55, 30, 2) 100%); -webkit-filter: drop-shadow(8px 10px 1px #000); /* 亮色
*/
   background-image:  var(--gold-image), linear-gradient(rgb(88, 82, 69) 0%, rgb(64, 38, 11) 30%, rgb(17, 10, 2) 100%);
   filter: drop-shadow(1px 10px 5px #422306);
   transform: rotateX(20deg);
   background-blend-mode: overlay, normal;
   background-size: cover, 100% 260%;
   border-radius: 0.5rem;

   margin: -0.1rem 0 0 0;
}

.et-plank p {
   margin-block-start: 0; /* 感谢 Jochoi 的修改 */
   -webkit-margin-before: 0;
   /*! line-height: 100%; */
}

/*
=====================
书封颜色
=====================
*/
.cover-green{
    background-image: var(--gold-image),
    linear-gradient(rgb(30, 55, 30) 0%, rgb(30, 55, 30) 30%, rgb(255, 219, 90) 100%);
    background-blend-mode: overlay, normal;
    background-size: cover, 100% 260%;
    border-left: 0.25rem double #e0ba45;
    border-right: 0.25rem double #e0ba45;

    -moz-box-shadow: var(--box-green);
    -o-box-shadow: var(--box-green);
    -webkit-box-shadow: var(--box-green);
    box-shadow: var(--box-green);
}

.cover-red {
   background-image:  var(--cloud-image), linear-gradient(rgb(186, 12, 12) 0%, rgb(145, 17, 17) 30%, rgb(102, 20, 20) 100%);
   background-blend-mode: overlay, normal;
   border-left: 0.25rem double #e0ba45;
   border-right: 0.25rem double #e0ba45;

  -moz-box-shadow: var(--box-red);
    -o-box-shadow: var(--box-red);
    -webkit-box-shadow: var(--box-red);
    box-shadow: var(--box-red);
}

.cover-black {
     background: linear-gradient(70deg, #191818, #636367,#333,#000);
     border-left: 0.25rem double #aba7a7;
     border-right: 0.25rem double #aba7a7;

   -moz-box-shadow: var(--box-black);
    -o-box-shadow: var(--box-black);
    -webkit-box-shadow: var(--box-black);
    box-shadow: var(--box-black);
}

.cover-galaxy {
    background-image:  var(--night-image), linear-gradient(rgb(132, 132, 132) 0%, rgb(136, 136, 205) 30%, rgb(36, 36, 36) 100%);
    border-left: 0.25rem double #9b01d3;
    border-right: 0.25rem double #9b01d3;

    -moz-box-shadow: var(--box-galaxy);
    -o-box-shadow: var(--box-galaxy);
    -webkit-box-shadow: var(--box-galaxy);
    box-shadow: var(--box-galaxy);
}

.cover-gold {
   background-image:  var(--wave-image), linear-gradient(rgb(223, 216, 31) 0%, rgb(202, 129, 0) 30%, rgb(206, 118, 12) 100%);
   border-left: 0.25rem double #58076c;
   border-right: 0.25rem double #58076c;

    -moz-box-shadow: var(--box-gold);
    -o-box-shadow: var(--box-gold);
    -webkit-box-shadow: var(--box-gold);
    box-shadow: var(--box-gold);
}

.cover-purple {
   background: linear-gradient(-80deg, #420a75, #8f00ca,#8137ca,#7b20c4,#490d78);
   border-left: 0.25rem double #edb016;
   border-right: 0.25rem double #edb016;

    -moz-box-shadow: var(--box-purple);
    -o-box-shadow: var(--box-purple);
    -webkit-box-shadow: var(--box-purple);
    box-shadow: var(--box-purple);
}

.cover-blue {
   background-image: linear-gradient(rgb(2, 151, 213) 0%, rgb(15, 77, 157) 30%, rgb(23, 26, 116) 100%);
   background-blend-mode: overlay, normal;
   background-size: cover, 100% 260%;
   border-left: 0.25rem double #28d8a2;
   border-right: 0.25rem double #28d8a2;

    -moz-box-shadow: var(--box-blue);
    -o-box-shadow: var(--box-blue);
    -webkit-box-shadow: var(--box-blue);
    box-shadow: var(--box-blue);
}

.cover-blue-2 {
   background-image: url(http://wanderers-library.wikidot.com/local--files/component:theme/goldfoil.png), linear-gradient(rgb(2, 151, 213) 0%, rgb(15, 77, 157) 30%, rgb(23, 26, 116) 100%);
   background-blend-mode: overlay, normal;
   background-size: cover, 100% 260%;
   border-left: 0.25rem double #28d8a2;
   border-right: 0.25rem double #28d8a2;

    -moz-box-shadow: var(--box-blue);
    -o-box-shadow: var(--box-blue);
    -webkit-box-shadow: var(--box-blue);
    box-shadow: var(--box-blue);
}

.cover-blue-3 {
   background-image:var(--bluepaper-image);
  /* background-blend-mode: overlay, normal;*/
   background-size: cover, 120% 100%;
   border-left: 0.25rem double #153d7b;
   border-right: 0.25rem double #153d7b;

   -moz-box-shadow: var(--box-bluedark);
   -o-box-shadow: var(--box-bluedark);
   -webkit-box-shadow: var(--box-bluedark);
   box-shadow: var(--box-bluedark);
}

.cover-darkblue{
    background: linear-gradient(72deg, #190a5e, #170358,#0a1064,#080d46,#0d1252,#170743);
    border-left: 0.25rem double #1fa8d8;
    border-right: 0.25rem double #1fa8d8;

    -moz-box-shadow: var(--box-darkblue);
    -o-box-shadow: var(--box-darkblue);
    -webkit-box-shadow: var(--box-darkblue);
    box-shadow: var(--box-darkblue);
}

.cover-white {
    background: linear-gradient(90deg, #d9d9d9, #e0f4f2,#dedede,#e5f1ed,#eee,#8e8e8e);
    border-left: 0.25rem double #606060;
    border-right: 0.25rem double #606060;

    -moz-box-shadow: var(--box-white);
    -o-box-shadow: var(--box-white);
    -webkit-box-shadow: var(--box-white);
    box-shadow: var(--box-white);
}

.cover-darkblack {
    background: linear-gradient(90deg, #2f2f2f, #555,#2a2727,#161717,#3a3939,#000);
    border-left: 0.25rem double #606060;
    border-right: 0.25rem double #606060;

    -moz-box-shadow: var(--box-black);
    -o-box-shadow: var(--box-black);
    -webkit-box-shadow: var(--box-black);
    box-shadow: var(--box-black);
}

.cover-darkgrey {
   background: linear-gradient(90deg, #5a5a5b, #6e6e6e,#8c8383,#6d7272,#656565,#585858);
   border-left: 0.25rem double #191818;
   border-right: 0.25rem double #191818;

   -moz-box-shadow: var(--box-black);
   -o-box-shadow: var(--box-black);
   -webkit-box-shadow: var(--box-black);
   box-shadow: var(--box-black);
}

.cover-mix-1 {
    background: linear-gradient(43deg, #1f0d00, #1f462b,#0a1064,#5a199d);
    border-left: 0.25rem double #65970c;
    border-right: 0.25rem double #65970c;

    -moz-box-shadow: var(--box-mix-1);
    -o-box-shadow: var(--box-mix-1);
    -webkit-box-shadow: var(--box-mix-1);
    box-shadow: var(--box-mix-1);
}

.cover-mix-2 {
    background: linear-gradient(-65deg, #580954, #320f7c,#518,#531f7b,#bb1a84);
    border-left: 0.25rem double #0c0f3e;
    border-right: 0.25rem double #0c0f3e;

    -moz-box-shadow: var(--box-purple);
    -o-box-shadow: var(--box-purple);
    -webkit-box-shadow: var(--box-purple);
    box-shadow: var(--box-purple);
}

/*
----------------
手机横排书角
----------------
*/
@media (max-width: 479px) {

  .trans-yes .dust-jacket{
     border-left: 0;
     border-right: 0;
  }

  .trans-yes .book-list p{
    height: 0;
  }

  .trans-yes  .cover-green{
    border-top: 0.25rem double #e0ba45;
    border-bottom: 0.25rem double #e0ba45;
  }

  .trans-yes  .cover-red {
    border-top: 0.25rem double #e0ba45;
    border-bottom: 0.25rem double #e0ba45;
   }

  .trans-yes  .cover-black {
    border-top: 0.25rem double #aba7a7;
    border-bottom: 0.25rem double #aba7a7;
  }

  .trans-yes  .cover-galaxy {
    border-top: 0.25rem double #9b01d3;
    border-bottom: 0.25rem double #9b01d3;
  }

  .trans-yes  .cover-gold {
    border-top: 0.25rem double #58076c;
    border-bottom: 0.25rem double #58076c;
  }

  .trans-yes  .cover-purple {
    border-top: 0.25rem double #edb016;
    border-bottom: 0.25rem double #edb016;
  }

  .trans-yes  .cover-blue {
    border-top: 0.25rem double #28d8a2;
    border-bottom: 0.25rem double #28d8a2;
  }

  .trans-yes  .cover-blue-2 {
    border-top: 0.25rem double #28d8a2;
    border-bottom: 0.25rem double #28d8a2;
  }

  .trans-yes  .cover-blue-3 {
    border-top: 0.25rem double #153d7b;
    border-bottom: 0.25rem double #153d7b;
  }

  .trans-yes  .cover-darkblue{
    border-top: 0.25rem double #1fa8d8;
    border-bottom: 0.25rem double #1fa8d8;
  }

  .trans-yes  .cover-white {
    border-top: 0.25rem double #606060;
    border-bottom: 0.25rem double #606060;
  }

  .trans-yes  .cover-darkblack {
    border-top: 0.25rem double #606060;
    border-bottom: 0.25rem double #606060;
  }

  .trans-yes  .cover-darkgrey {
    border-top: 0.25rem double #191818;
    border-bottom: 0.25rem double #191818;
  }

  .trans-yes  .cover-mix-1 {
    border-top: 0.25rem double #65970c;
    border-bottom: 0.25rem double #65970c;
  }

  .trans-yes  .cover-mix-2 {
    border-top: 0.25rem double #0c0f3e;
    border-bottom: 0.25rem double #0c0f3e;
 }
}

/*
=====================
书籍链接颜色
=====================
*/

.link-gold a {
    color:#e0ba45;
}

.link-white a {
   color:#f4f4f4;
}

.link-red a {
   color:#b01;
}

.link-black a {
   color:#000;
}

/*
=====================
书架内的滚动条，火狐无效
=====================
 */

.shelf-wrap {
    scrollbar-color: #976f23 #805213;
}

.shelf-wrap::-webkit-scrollbar {
    height: 0.5rem !important;
    background-color: #F5F5F5;
}

.shelf-no .list-pages-box,
.shelf-yes .list-pages-box{
    scrollbar-width:none;
    width: 100%;
}

/*
.shelf-no, .shelf-yes{
}
*/

/* 电脑上的火狐无效
.shelf-no ::-webkit-scrollbar{
        background-color:#b5b1b1;  
}

.shelf-no ::-webkit-scrollbar-thumb {  
    border-radius: 10px;  
