/*
=============================
手机下的描边
============================
*/

@media (max-width: 479px) {

  .shelf-yes, .shelf-yes .list-pages-box {
    display:  inline-block;
    overflow-y: visible;
    margin: 0 0rem 0 0rem;
    padding: 0rem 0 0 0;
    width:100%;
  }

  .trans-yes{
     display:  flex;
     margin:2rem 0 5rem  0rem ;
   }

  .trans-yes  .et-plank-wrap{
        display:  inline-flex;
        margin: 0;
    }

   .trans-yes  .et-plank-wrap .et-plank{
        width: 2rem;
        height: 115%;
        margin: -2rem 0 0 0;
        background-image: var(--gold-image),
             linear-gradient(to top left, rgb(64, 38, 11) 100%, rgb(17, 10, 2) 90%),rgb(88, 82, 69) 100%);

    }

   .shelf-yes .dust-jacket {  
      width:100%;
      min-height: 3rem;    
      margin: 0 0 1rem 0rem;
      display: block;
      transition: all 1s;
      filter: brightness(1) drop-shadow(13px 5px 1px #333);
      -webkit-filter: brightness(1) drop-shadow(10px 10px 2px #333);
      border-radius: 0.2rem;
      padding: 0;
  }

   .shelf-yes .dust-jacket p{
       /*! line-height: 8; */
   }

  .shelf-yes  .dust-jacket:hover{
      margin-left:-1rem;
      margin-right:0rem;
   }

 .shelf-yes .book-list a {
     position:relative ;
     margin: -6rem 0 0 0;
     left: 7rem;
     height:12rem;
     padding:  0rem 0rem 0rem 0rem;
  }

  .shelf-yes .pager {
      writing-mode:   horizontal-tb  !important;  
      padding: 4rem 0 0 0;
      line-height: 2;
      width: calc(100% - 3rem);
  }

   .shelf-yes .book-list a,
   .shelf-yes .float-info  {
       transform:rotate(-90deg);
      -ms-transform:rotate(-90deg);
      -webkit-transform:rotate(-90deg);
   }

   .trans-yes{
        padding: 0;
    }

   .shelf-no    .dust-jacket:hover{
      -webkit-filter: brightness(2) drop-shadow(6px 20px 1px #000);
      filter: brightness(2) drop-shadow(6px 20px 1px #333);   /* 如果把最后的1改成0，可能会有聚焦效果，但可能也更卡*/
      transform:rotateX(20deg);
      -webkit-transform:rotateX(-25deg); /* Safari and Chrome */
      -moz-transform:rotateX(-25deg); /* Firefox */
      transition: all 1s;
      margin-bottom:2rem;
      margin-top:-2rem;
   }
}

