/*
=====================
书籍描述
=====================
*/

/* 方案一  */
.book-description {
    position:absolute;
    z-index:-1;
    margin-top: 14.8rem;
    color: darkkhaki;
    white-space: nowrap;/* 书本信息不分行，待思考 */
    opacity: 0;
    transition: opacity  0.5s;
    float: left;
}

.self-defining .book-description, 
.list-pages-box .book-description{
    margin-top: -0.4rem;
}

.book-description p{
   padding: 0 1rem 0 0.5rem;
   overflow: hidden;
   text-overflow:ellipsis;
}

.dust-jacket:hover + .book-description{
    z-index: 1;
    opacity: 1;
   transition: opacity 0.8s;
}

@media (max-width: 479px) {
   .shelf-yes .float-info {
   top:21rem;
    }

    .shelf-yes .book-description{
        position:relative;
         right: -3.2rem;
         writing-mode: vertical-lr;
         margin: -5rem 0 0 0; /* 若是排到左侧的话会更好 */   
        float: right;
}

        .shelf-yes .list-pages-box .book-description{
        position:fixed;
}

/*
------
当在手机屏幕下文字正排时，可旋转文字使之横排
    .shelf-yes    .book-description{
       transform:rotate(-90deg);
    -ms-transform:rotate(-90deg);
    -webkit-transform:rotate(-90deg);

}
------
*/

  .self-defining  .book-description {
      left:unset;
   }  

  .self-defining .trans-yes .book-description {
      right: -10rem !important;
      margin: 0rem 0 0 0 !important;
      writing-mode: vertical-lr;
      left:0rem;
  }  

   .shelf-yes  .list-pages-box .book-description {
       margin: 0rem 0 0 0;
       writing-mode:  sideways-lr !important;  
       right: -1rem;
        transform:rotate(0deg);
    -ms-transform:rotate(0deg);
-webkit-transform:rotate(0deg);
    }     
}

/*
方案二
部分代码参考这里
http://scp-wiki-cn.wikidot.com/component:author-page
*/

.float-info{
  margin: 20px;
  overflow:hidden;
  position:fixed;
  right: 1rem;
  bottom: 1rem;
  width: 21%;
  width: calc(50vw - 520px);
  min-width: var(--book-height);
  opacity:0;
  transition: all 0.7s ease-out;
  z-index: -1;
}

.dust-jacket:hover+ .book-description + .float-info{
    opacity: 1;
    transition: opacity 0.5s;
    z-index: 1;
}

.float-info:hover{
        opacity: 1;
        z-index: 0;
}

@media screen and (max-width:992px) {
  .float-info {
     margin: 0 auto;
     right: 0;
     bottom: 0;
    min-height:2rem;/* 高度 */
  }
}

.info-1{
   padding:1%;
   box-shadow: 0px 4px 8px 0 rgba(0, 0, 0, 0.2), 0px 6px 20px 0 rgba(0, 0, 0, 0.19);
   background: rgba(242,242,194,1);
   background-image: var(--wave-image);
   border:   0.5em double rgba(167,105,68,0.9);
}

