[[module CSS]]
.scp-block {
    border: none;
    box-shadow: 0 0 10px rgba(0, 0, 0, 0.2);
    background-color: #fff;
    padding-top: 0.5em;
    padding-right: 15px;
    padding-left: 15px;
    padding-bottom: 1em;
    color: #000;
}

.scp-block {
    background-image: url('http://scpkrsandbox.wikidot.com/local--files/wallowing-d/scp-block-background.png');
    background-position: 50% 50%;
    background-repeat: no-repeat;
    background-size: 200px;
}

.scp-0-caption {
    border: none;
    background-color: none;
    padding-top: 0em;
    padding-right: 15px;
    padding-left: 15px;
    padding-bottom: 0em;
    color: #000000;
}

.scp-1-caption {
    border-left: solid 3px #000;
    border-right: solid 3px #000;
    background-color: none;
    padding-top: 0em;
    padding-right: 15px;
    padding-left: 15px;
    padding-bottom: 0em;
    color: #000000;
}

.scp-2-caption {
    border-top: double 5px #000;
    border-bottom: double 5px #000;
    background-color: none;
    padding-top: 0em;
    padding-right: 15px;
    padding-left: 15px;
    padding-bottom: 0em;
    color: #000000;
}

.scp-3-caption {
    border: none;
    background-color: none;
    padding-right: 15px;
    padding-left: 15px;
    color: #000000;
}
[[/module]]

