}

.wide,
.lightmode {
  display: block;
}
.mobile,
.light1, .darkmode {
  display: none;
}

.dark1 {
  display: block;
}

@media (max-width: 1059px) {

  .wide {
    display: none;
  }
  .mobile {
    display: block;
  }

}

