[[iftags +组件]]

[[mOdUlE cSs]]
.step_container {
    margin: 0.5rem 0;
    border: 1px solid #999;
    padding: 1em;
}

.help {
    display: flex;
    align-content: stretch;
    padding-bottom: 1em;
    border-bottom: 1px dashed #999;
}

.help blockquote {
    margin: 0;
    font-size: 85%;
}

.help blockquote > p {
    display: flex;
    flex-wrap: wrap;
    align-items: center;
    align-content: center;
    margin-right: 0;
    min-height: 2rem;
    margin: 0;
}

.help blockquote > p * {
    font-size: 0.5rem;
    margin: 0 0.1rem;
    height: 100%;
}

.help blockquote:nth-of-type(1) {
    width: 30%;
    background: transparent;
    border: unset;
    box-shadow: unset;
    text-align: right;
    margin-right: 0;
    padding: 0;
}

.help blockquote:nth-of-type(1) > p {
    justify-content: flex-end;
}

.help blockquote:nth-of-type(2) {
    margin-left: 1em;
    width: 70%;
}
.collapsible-block-unfolded-link, .collapsible-block-folded {
    text-align: center;
}
.collapsible-block-content {
    text-align: left;
}
.show_container {
    display: flex;
    justify-content: flex-start;
    flex-wrap: wrap;
    border: 1px solid #999;
    padding-bottom: 0.5rem;
}
.show_container>div {
    width: calc(100% / 4);
    text-align: center;
}
[[/mOdUlE]]
