	color: rgb(var(--swatch-primary,126,27,1));
}
div.blockquote {
	padding-top: 0.5em;
}
div.blockquote tt {
	margin: 0.5em 1em 1em 1em;
	display: block;
	background-color: rgb(var(--dark-gray-monochrome,48,48,52));
	color: rgb(var(--white-monochrome,252,252,252));
	padding: 0.5em;
}
