[[div class="earthworm"]]
[[div class="first {$first}"]]
  [[div class="inner"]]
    [[image https://scp-wiki.wdfiles.com/local--files/component%3Aearthworm/Transparent.gif]]

    [[div class="arrow"]]
    [[/div]]

  [[/div]]

  [{$previous-url} {$previous-title}]
[[/div]]
[[div class="hub {$hub}"]]
  [{$hub-url} {$hub-title}]
[[/div]]
[[div class="last {$last}"]]
  [[div class="inner"]]
    [[image https://scp-wiki.wdfiles.com/local--files/component%3Aearthworm/Transparent.gif]]

    [[div class="arrow"]]
    [[/div]]

  [[/div]]

  [{$next-url} {$next-title}]
[[/div]]
[[/div]]

[[module CSS]]
.earthworm {
    font-weight: bold;
    display: flex;
    justify-content: center;
    align-items: stretch;
    margin: 1em 0;
    clear: both;
    z-index: 0;
    min-height: 36px;
}
.earthworm > div {
    display: flex;
    align-items: center;
}
.earthworm p {
    margin: 0 0.8em;
    text-align: center;
    position: relative;
    z-index: 5;
}
.earthworm .first {
    border-top: 2px rgba(150,150,150,0.5) solid;
    border-bottom: 2px rgba(150,150,150,0.5) solid;
    position: relative;
}
.earthworm .first:not(.true) img {
    height: 100%;
    width: auto;
    display: block;
    position: absolute;
    left: 0;
    top: -1.5px;
    border: 2.8px rgba(150,150,150,0.5) solid;
    transform: scale(0.707) rotate(-45deg);
    border-width: 2.8px 0 0 2.8px;
    transform-origin: 0% 100%;
}
.earthworm .hub {
    margin: -3px 0;
    border: 3px rgba(150,150,150,0.5) solid;
    background: linear-gradient(10deg, rgba(150,150,150,0.1), rgba(150,150,150,0));
    position: relative;
    z-index: 5;
}
.earthworm .hub.no a,
.earthworm a[href="/"] {
    color: #333;
    text-decoration: none;
    cursor: text;
    pointer-events: none;
}
.earthworm .last {
    border-top: 2px rgba(150,150,150,0.5) solid;
    border-bottom: 2px rgba(150,150,150,0.5) solid;
    position: relative;
}
.earthworm .last:not(.true) img {
    height: 100%;
    width: auto;
    display: block;
    position: absolute;
    right: 0;
    top: -1.5px;
    border: 2.8px rgba(150,150,150,0.5) solid;
    transform: scale(0.707) rotate(45deg);
    border-width: 2.8px 2.8px 0 0;
    transform-origin: 100% 100%;
}
.earthworm div.true {
    width: 0;
}
.earthworm div.true p {
    display: none;
}
.earthworm a {
    word-break: normal;
}
[[/module]]

