[[module css]]
/* DISRUPTION CLASS */
 .disruption-header{
    display: inline-block;
    text-align: center;
    width: 100%;
    margin: 0;
    padding: .3em 0;
    font-size: 140%;
    text-transform: uppercase;
    color: rgb(252, 252, 252);
    text-shadow: 1px 3px 1px rgba(0, 0, 0, .3);
}
 .disruption-header a:hover{
    text-decoration: none;
}

.lang-cn .lang-tr,
.lang-tr .lang-cn {
    display: none;
}
[[/module]]

[[a href="*http://scp-wiki-cn.wikidot.com/anomaly-classification-system-guide"]]
  [[span_ class="disruption-header lang-{$lang}" style="background-color: {$disruption-bg-color}; color: {$disruption-text-color};"]]
