content:
[[module CSS]]
/* 点击这里查看实际效果：
 * http://scp-wiki-cn.wikidot.com/component:flops-header
**/

/* ---- ITEM INFO ---- */
 .itemInfo{
     position: relative;
     top: .5em;
     width: 100%;

     font-size: 135%;
     text-transform: uppercase;
     white-space: nowrap;
}
 .itemInfoHeading{
     font-weight: 900;
}
 .alignL{
     position: relative;
     right: .195em;
}
 .alignR{
     position: relative;
     left: .195em;

     text-align: right;
}
 .infofield_1,
 .infofield_2{
     font-weight: inherit;
}

/* ---- DISRUPTION CLASS ---- */
 a.disruptionHeader{
     display: inline-block;
     width: 100%;
     padding: .3em 0;

     color: hsl(0, 0%, 91.5%);

     font-size: 145%;
     text-align: center;
     text-decoration: none;
     text-shadow:
          .5px    .9px  0 hsla(0, 0%, 0%, .3),
        1.5px   2.6px  0 hsla(0, 0%, 0%, .3);
     text-transform: uppercase;
     transition: color .15s linear;
}
 a.disruptionHeader:hover{
     color: white;
     background-color: transparent;
     text-decoration: none;
}

.lang-cn .lang-tr,
.lang-tr .lang-cn {
    display: none;
}
[[/module]]

[[div]]
[[table class="itemInfo darkbox lang-{$lang}"]]
  [[row]]
    [[cell class="alignL"]]
      [[span class="itemInfoHeading bt"]][[span class="infofield_1"]][[span class="lang-cn"]]项目编号：[[/span]][[span class="lang-tr"]]項目編號：[[/span]][[/span]][[/span]]SCP-{$item}
    [[/cell]]
    [[cell class="alignR"]]
      [[span class="itemInfoHeading bt"]]{$level}/{$item} [[span class="lang-cn"]]级[[/span]][[span class="lang-tr"]]級[[/span]][[/span]]
    [[/cell]]
  [[/row]]
  [[row]]
    [[cell class="alignL"]]
      [[span class="itemInfoHeading bt bh"]][[span class="infofield_2 bh"]][[span class="lang-cn"]]收容等级：[[/span]][[span class="lang-tr"]]收容等級：[[/span]][[/span]][[/span]]{$containment}
    [[/cell]]
    [[cell class="alignR"]]
      [[span class="itemInfoHeading bt"]]{$security}[[/span]]
    [[/cell]]
  [[/row]]
[[/table]]

[[a href="*/anomaly-classification-system-guide" class="disruptionHeader lang-{$lang}" style="background-color: {$disruption-bg-color};"]][[span style="font-weight: 900;"]][[span class="lang-cn"]]扰动等级：[[/span]][[span class="lang-tr"]]擾動等級：[[/span]][[/span]]{$disruption}[[/a]]
[[/div]]
