[[module CSS]]

@import url('https://scp-ko-15c.wikidot.com/local--files/component:theme/NBG-OldKo.css');

@font-face {font-family: 'EBSHMJESaeronRA'; src: url('https://cdn.jsdelivr.net/gh/projectnoonnu/noonfonts_one@1.0/EBSHMJESaeronRA.woff') format('woff'); font-weight: normal; font-style: normal; }

@font-face {font-family: ‘cwTeXFangSong’; src: url('https://fonts.gstatic.com/ea/cwtexfangsong/v3/cwTeXFangSong-zhonly.woff') format('woff'); font-weight:normal; font-style: normal;}

@import url('https://fonts.googleapis.com/css2?family=Noto+Serif+TC:wght@400;700&display=swap');

 :root {
     --button-0: none;
     --button-1: inline-block;
}

html {
  overflow-x: hidden;
}

 .gotext {
     width:33px;
     height: 32px;
     font-family: 'EBSHMJESaeronRA', ‘cwTeXFangSong’, 'Noto Serif TC', 'NanumBarunGothic YetHangul', serif;
     font-size: 20px;
     text-align: center;
     direction: ltr;
     border: 1px solid darkred;
     transform: rotate(-89.9deg) scaleY(-1);
     position: relative;
}

 .gotext strong {
     font-size: 130%;
}

 .gotext sup:not(.footnoteref), 
 .gotext sub {
     position: relative;
     font-size: 60%;
     z-index: 1;
     top: -0.5em;
     right: -0.5em;
}

 .gotext sup.footnoteref {
     position: absolute;
     top: 15px;
     left: -12px;
}

 .gotext a.footnoteref {
     display: inline-block;
     font-family: verdana, "Nanum Gothic", arial, helvetica, sans-serif;
     font-size: 11px;
     text-decoration: none;
     position: relative;
     top: 5px;
     left: 23px;
     margin-left: -10px;
}

 .gocomment {
     height:16px;
     font-family: 'EBSHMJESaeronRA', ‘cwTeXFangSong’, 'Noto Serif TC', 'NanumBarunGothic YetHangul', serif;
     font-size: 15px;
     text-align: left;
     vertical-align: center;
     border: 1px solid darkred;
     transform: scaleX(-1);
     position: relative;
}

 .comrotate {
     width: 12px;
     display: inline-block;
     writing-mode: vertical-lr;
     transform: rotate(-89.9deg) translateX(2px);
     transform-origin: top right;
     position: absolute;
     height: 100vh;
}

 .godiv {
     font-family: verdana, "Nanum Gothic", arial, helvetica, 'NanumBarunGothic YetHangul', sans-serif;
}

 .godisplay {
     max-width: 1500px !important;
     width: 100%;
     text-align: center;
     display: inline-flex;
     flex-direction: row-reverse;
     flex-wrap:wrap;
     justify-content: flex-start;
     align-content: space-between;
     margin-bottom: 2em;
}

 .godisplay::after {
     content: "";
     position: relative;
     order: 10000;
     margin: 0.5em auto;
     width: 324px;
     height: 0px;
     padding: 0 7px;
}

 .popup-button {
     width: 100%;
     color: #000000;
     padding: 0px;
     border: none;
     background: transparent;
     text-decoration: none;
     cursor: pointer;
     transition: all 0.3s;
     font-size: 120%;
     position: absolute;
     bottom: 0;
     left: 10px;
     transform: translateY(25px);
}

 .popup-button:hover {
     background: transparent;
}

 .overlay {
     display: block;
     position: fixed;
     top: 0;
     bottom: 0;
     left: 0;
     right: 0;
     background: rgba(100, 100, 100, 0.5);
     transition: opacity 500ms;
     visibility: hidden;
     opacity: 0;
     z-index: 100;
}

 .overlay:target {
     visibility: visible;
     opacity: 1;
}

 .popup {
     margin: 7% 29.5% 0 40.5%;
     padding: 20px;
     background: #fff;
     border-radius: none;
     width: 30%;
     min-width: 500px;
     max-height: 70%;
     overflow:auto;
     position: relative;
     border: #B01 solid 2px;
}

 .popup h2 {
     font-family: 'EBSHMJESaeronRA', ‘cwTeXFangSong’, 'Noto Serif TC', 'NanumBarunGothic YetHangul', serif;
     margin: 0 auto 35px auto;
     color: darkred;
}

 .popup .close-popup {
     position: fixed;
     top: 0%;
     margin-top: 8%;
     right: 29%;
     transition: all 0ms;
     font-size: 20px;
     font-weight: 900;
     text-decoration: none;
     color: #333;
     text-shadow: 1px 0 #fff, -1px 0 #fff, 0 1px #fff, 0 -1px #fff;
}

 .popup .close-popup:hover {
     color: #B01;
}

 .popup .popup-content {
     width: 90%;
     margin: 10px auto;
     padding-top: 10px;
     overflow: auto;
     text-align: center;
     font-family: 'EBSHMJESaeronRA', ‘cwTeXFangSong’, 'Noto Serif TC', 'NanumBarunGothic YetHangul', serif;
     font-size: 17px;
     background: #FBF4EB;
     box-shadow: 0px 0px 20px 5px #FBF4EB;
}

 .popup .popup-content strong {
     font-size: 120%;
}

 .popup .popup-content sup,
 .popup .popup-content sub {
     font-size: 70%;
}

 .popup .popup-translation {
     overflow: auto;
     text-align: center;
     font-family: verdana, "Nanum Gothic", arial, helvetica, sans-serif;
     font-size: 14px;
     margin: 30px;
}

 .popup .popup-translation sub {
     font-size: 11px;
}

 .popup .popup-content p {
     margin-top: -5px;
     line-height: 1;
}
 .popup .popup-translation p {
     line-height: 1.2;
}

 .popup .popup-footnote {
     overflow: auto;
     text-align: left;
     font-family: verdana, "Nanum Gothic", arial, helvetica, sans-serif;
     font-size: 12px;
     line-height: 1.6;
}

 .popup .popup-footnote sub {
     font-size: 11px;
}

 .footnotes-footer div {
     margin: 10px 0;
}

 .f-footer {
     display: none;
}

 .f-heading {
     color: transparent;
}

 .f-heading::before {
     content: "註";
     color: #333;
}

 .f-content {
     max-width: 500px;
}


 @media screen and (max-width: 1667px) {
   .popup .close-popup {
        right: calc( 57vw - 480px );
    }
}

 @media screen and (max-width: 1025px){
    .popup{
        margin: 6% auto;
        width: 50%;
        min-width: 400px;
        max-height: 75%;
    }
    .popup .close-popup {
        margin-top: 7%;
        right: 27%;
    }
    .godisplay {
        width: fit-content;
    }
}

@media screen and (max-width: 767px){
    .popup{
        margin: 5% auto;
        width: 70%;
        min-width: 300px;
        max-height: 80%;
    }
   .popup .close-popup {
        right: 15%;
    }
}
[[/module]]

[[div style="display:none;"]]
[[#ifexpr {$center} == 1 |  | [!-- ]]
[[module CSS]]

@media (min-width: 768px) {
  .popup {
    margin: 7% auto 0;
  }
  .popup .close-popup {
    right: 50%;
    transform: translateX(calc(150px + 6vw));
  }
}

[[/module]]
