content:
[[module CSS]]
.scp-image-block.descr {
    position: relative;
}

.scp-image-block.descr .overlay {
    position: absolute;
    top: 0;
    left: 0;
    height: 100%;
    width: 100%;
    padding: 2rem;
    -webkit-box-sizing: border-box;
            box-sizing: border-box;
    overflow: scroll;
    opacity: 0;
    -webkit-transition: .2s cubic-bezier(.46, .03, .52, .96);
    -o-transition: .2s cubic-bezier(.46, .03, .52, .96);
    transition: .2s cubic-bezier(.46, .03, .52, .96);
    background-color: rgba(0,0,0,.7);
    -webkit-backdrop-filter: blur(2px);
    backdrop-filter: blur(2px);
}

.scp-image-block.descr .overlay-text {
    color: #fff;
    text-align: center;
}

.scp-image-block.descr .overlay-text h1 {
    color: inherit;
}

.scp-image-block.descr .overlay:hover {
    opacity: 1;
}
[[/module]]

[[div class="scp-image-block descr block-{$align}" style="width:{$width};"]]
[[image {$name} style="width:{$width};" link={$link}]]
[[div class="overlay"]]
[[div class="overlay-text"]]
{$overlay-text}
[[/div]]
[[/div]]
[[div class="scp-image-caption" style="width:{$width};"]]
{$caption}
[[/div]]
[[/div]]
