content:
[[module css]]
/* Dépliement */
.bselector .collapsible-block-folded, .bselector .collapsible-block-unfolded-link {
    text-align: center;
}
.bselector .collapsible-block-folded a.collapsible-block-link {
    transition: 200ms ease-in;
}
.bselector .collapsible-block-folded a.collapsible-block-link:before {
    content: "►  "
}
.bselector .collapsible-block-folded a.collapsible-block-link:after {
    content: " ◄"
}
.bselector .collapsible-block-unfolded a.collapsible-block-link:before {
    content: "▼ "
}
.bselector .collapsible-block-unfolded a.collapsible-block-link:after {
    content: " ▼"
}

.bselector .collapsible-block-link {
    font-weight: bold;
    text-align: center;
    font-size: 1.1rem;
    text-align: center;
}
.bselector .collapsible-block {
    margin: 1rem 2rem;
}

/* Module de navigation */

.bselector__entries {
    display: flex;
    flex-wrap: wrap;
    justify-content: center;
    padding-bottom: 1rem;
}
.bselector__entries p:hover {
    transform: scale(1.18);
}
.bselector__entries p {
    transition: transform ease-in 0.3s;
}

/* Toutes les branches */

.int .bselector--int::before {
    background-image: url(http://o5command-int.wdfiles.com/local--files/tech-team:graphic-templates/scp-logo-int-full.svg);
}
.int .bselector--int {
    display: grid;
}
.bselector--cn::before {
    background-image: url(https://raw.githubusercontent.com/Grombald/Common-Theme/master/src/svg_inkscape/scp-logo-cn.svg);
}
.cn .bselector--cn {
    display: grid;
}
