    display: grid;
}

/* Chaque entrée */

.bselector__entry::before {
    content: "";
    position: absolute;
    filter: brightness(0.8);
    width: 6em;
    height: 6em;
    background-size: contain;
}
.bselector__entry {
    text-align: center;
    display: grid;
    align-content: center;
    text-shadow: 2px 0 0 #fff, -2px 0 0 #fff, 0 2px 0 #fff, 0 -2px 0 #fff, 1px 1px #fff, -1px -1px 0 #fff, 1px -1px 0 #fff, -1px 1px 0 #fff;
    font-weight: bold;
    font-size: 1.2em;
    margin-inline: 0.33rem;
    position: relative;
    width: 6em;
    height: 6em;
    display: none;
}
.bselector__entry:hover {
    text-decoration: none;
}
.bselector__entry__text {
    position: relative;
    color: black;
}


.bselector p {
    margin: 0;
}
