[[module CSS]]

.c_international-container .panel-body {
    padding: 0;
}
.c_international-body {
    display: flex;
    justify-content: space-evenly;
    flex-wrap: wrap;
    *background: rgba(255,255,255,.9) url('https://scp-wiki.wdfiles.com/local--files/component%3Atheme/panel-bg-gradient-reverse.png') bottom repeat-x;
    padding: 5px 0;
}
.c_international-box {
    margin: 5px 4px;
    text-align: center;
    min-width: 12em;
}
.c_international-box div > p,
.international-entry div > p {
    margin: 0;
}
.c_international-box .c_international-text {
    margin: 5px 0;
    font-size: 99%;
}
.international-entry {
    padding: 10px 10px 5px 10px;
    border-top: 1px solid #eeeee0;
}
.international-entry .international-description {
    margin: 5px 20px;
    font-size: 99%;
}
.c_international-box .c_international-logo img {
    height: 90px;
}


@media (min-width: 768px) and (max-width: 979px) {
    .c_international-box .c_international-logo img {
        height: 80px;
    }
    .c_international-box {
        min-width: 10em;
    }
}
@media (min-width: 581px) and (max-width: 767px) {
    .c_international-box .c_international-logo img {
        height: 70px;
    }
    .c_international-box {
        min-width: 10em;
    }
}
@media (min-width: 480px) and (max-width: 580px) {
    .c_international-box .c_international-logo img {
        height: 60px;
    }
    .c_international-box {
        min-width: 10em;
    }
}
@media (max-width: 479px) {
    .c_international-box .c_international-logo img {
        height: 50px;
    }
    .c_international-box {
        min-width: 10em;
    }
}

.content-panel p { padding: 0 0.75em; }

div.content-panel.centered.standalone p span{
    line-height: 1;
}
:root {
  --c_international-img-height: 90px;
  --c_international-box-width: 12em;
}

.c_international-container .panel-body {
    padding: 0;
}
.c_international-body {
    display: flex;
    justify-content: center;
    flex-wrap: wrap;
}
.c_international-box {
    text-align: center;
    width: var(--c_international-box-width);
}
.c_international-box:first-of-type {
    /*width: calc(2 * var(--c_international-box-width));*/
}
.c_international-box div > p {
    margin: 0;
}
.c_international-box .c_international-text {
    margin: 5px 0;
    font-size: 99%;
}
.c_international-box .c_international-logo img {
    height: var(--c_international-img-height);
}


@media (min-width: 768px) and (max-width: 979px) {
  :root {
    --c_international-img-height: 80px;
    --c_international-box-width: 10em;
  }
}
@media (min-width: 581px) and (max-width: 767px) {
  :root {
    --c_international-img-height: 70px;
    --c_international-box-width: 10em;
  }
}
@media (min-width: 480px) and (max-width: 580px) {
  :root {
    --c_international-img-height: 60px;
    --c_international-box-width: 10em;
  }
}
@media (max-width: 479px) {
  :root {
    --c_international-img-height: 50px;
    --c_international-box-width: 10em;
  }
  .c_international-box:first-of-type {
    flex-basis: 100vw;
  }
}

.translation-info {
    padding: 10px;
    background-color: #fffefe;
    border-bottom: solid 1px #eeeee0;
}
[[/module]]

