content:
[[module css]]
/* 汉化注：网络字体 ChillKai 与 GenRyuMin */
@import url('https://cdn.jsdelivr.net/gh/WinterholdPrime/webfont_ChillKai@main/ChillKai.css');
@import url('https://cdn.jsdelivr.net/gh/WinterholdPrime/webfont_GenRyuMin@main/GenRyuMin_Regular.css');
@import url('https://cdn.jsdelivr.net/gh/WinterholdPrime/webfont_GenRyuMin@main/GenRyuMin_Bold.css');

body {
  -webkit-text-size-adjust: 110%;
  font-family: var(--body-font, var(--page-font, arial, helvetica, sans-serif));
}
.vpoem {
  text-indent: 0;
  -webkit-writing-mode: vertical-rl;
          writing-mode: vertical-rl;
  margin: auto;
  margin-bottom: 0.5em;
  font-size: 2.5em;
  font-family: "KaiTi", "ChillKai", "MS PMincho", "STKaiti", "HiraMinProN-W6", "Hiragino Kaku Gothic Pro", "MS PGothic", serif;
}
.vpoem p {
  line-height: 1.4;
  margin: 0 auto;
}
.vpoem tt {
  vertical-align: middle;
  font-family: "KaiTi", "ChillKai", "MS PMincho", "STKaiti", "HiraMinProN-W6", "Hiragino Kaku Gothic Pro", "MS PGothic", serif;
}
@media screen and (max-width: 767px) {
  .vpoem {
    font-size: 1.5em;
  }
}

/* 汉化注：分注 */
span[style="text-decoration: line-through;"] {
  display: -webkit-inline-flex;   /* Safari */
  display:         inline-flex;
  -webkit-flex-direction: column; /* Safari */
          flex-direction: column;
  vertical-align: bottom;
}
span[style="text-decoration: line-through;"] > tt {
  font-size: 50%;
  line-height: 0.5em;
}
span[style="text-decoration: line-through;"] tt {
  text-decoration: none; /* 汉化注：消除横线 */
}
.tategaki span[style="text-decoration: line-through;"] > tt {
  line-height: 1em;
}
.tategaki span[style="text-decoration: line-through;"] {
  vertical-align: 0.3em;
}
.tategaki blockquote {
  padding: 2em 0.5em;
}
.tategaki tt {
  vertical-align: middle;
  font-family: "ヒラギノ明朝 Pro W3", "HG明朝E", "ＭＳ Ｐ明朝", "ＭＳ 明朝", "GenRyuMin", serif;
}
.tategaki .henoji:before {
  content: "〳〵";
  color: rgba(51, 51, 51, 1.0);
  opacity: 1;
  transform: scale(1, 1)rotateX(0) translateY(0);
}
.tategaki .dhenoji:before {
  content: "〴〵";
  color: rgba(51, 51, 51, 1.0);
  opacity: 1;
  transform: scale(1, 1)rotateX(0) translateY(0);
}
.tategaki .dhenoji:after {
  content: "";
  color: rgba(51, 51, 51, 1.0);
  opacity: 1;
  transform: scale(1, 1)rotateX(0) translateY(0);
}
.tategaki {
  -webkit-columns-axis: vertical;
     -moz-columns: auto 20em;
     -moz-column-gap: 20px;
     -moz-column-rule: 1px solid #dddddd;
     -moz-column-fill: auto;
  -webkit-columns: auto 20em;
  -webkit-column-gap: 20px;
  -webkit-column-rule: 1px solid #dddddd;
  -webkit-column-fill: auto;
          columns: auto 20em;
          column-gap: 20px;
          column-rule: 1px solid #dddddd;
          column-fill: auto;
  font-size: small;
  overflow-y: scroll;
  width: calc(100% + 3em);
  padding-right: 2em;
          writing-mode: tb-rl;
          writing-mode: vertical-rl;
     -moz-writing-mode: vertical-rl;
  -webkit-writing-mode: vertical-rl;
  font-family: "ヒラギノ明朝 Pro W3", "HG明朝E", "ＭＳ Ｐ明朝", "ＭＳ 明朝", "GenRyuMin", serif;
}
.tategaki {
  -webkit-column-axis: vertical;
     -moz-columns: auto 20em;
     -moz-column-gap: 20px;
     -moz-column-rule: 1px solid #dddddd;
     -moz-column-fill: auto;
  -webkit-columns: auto 20em;
  -webkit-column-gap: 20px;
  -webkit-column-rule: 1px solid #dddddd;
  -webkit-column-fill: auto;
          columns: auto 20em;
          column-gap: 20px;
          column-rule: 1px solid #dddddd;
          column-fill: auto;
  font-size: small;
  width: calc(100% + 3em);
  padding-right: 2em;
          writing-mode: tb-rl;
          writing-mode: vertical-rl;
     -moz-writing-mode: vertical-rl;
  -webkit-writing-mode: vertical-rl;
  font-family: "ヒラギノ明朝 Pro W3", "HG明朝E", "ＭＳ Ｐ明朝", "ＭＳ 明朝", "GenRyuMin", serif;
}
@media screen and (-webkit-min-device-pixel-ratio:0) {
  .tategaki {
    overflow-y: scroll;
  }
}

/* 汉化注：多字符叠字符号 */
.henoji:before {
  content: "へ";
  color: rgba(51, 51, 51, 1.0);
  opacity: 1;
  transform: scale(1.9, 1)rotateX(180deg) translateY(-0.08em);
  position: absolute;
  display: inline-block;
          user-select: none;
     -moz-user-select: none;
  -webkit-user-select: none;
      -ms-user-select: none;
  z-index: 1;
}
.henoji {
  position: relative;
  width: 1em;
  white-space: pre;
  display: inline-block;
  padding: 0 0.5em;
  color: rgba(51, 51, 51, 0);
}
.dhenoji:before {
  content: "へ";
  color: rgba(51, 51, 51, 1.0);
  opacity: 1;
  transform: scale(1.9, 1)rotateX(180deg) translateY(-0.08em);
  position: absolute;
  display: inline-block;
          user-select: none;
     -moz-user-select: none;
  -webkit-user-select: none;
      -ms-user-select: none;
  z-index: 1;
}
.dhenoji {
  position: relative;
  width: 1em;
  white-space: pre;
  display: inline-block;
  padding: 0 0.5em;
  color: rgba(51, 51, 51, 0);
}
.dhenoji:after {
  position: absolute;
  color: rgba(51, 51, 51, 1.0);
  content: "゛";
  top: 0.07em;
  left: 0.6em;
  z-index: 1;
}
.hdhenoji:before {
  content: "へ";
  color: rgba(51, 51, 51, 1.0);
  opacity: 1;
  transform: scale(1.9, 1)rotateX(180deg) translateY(-0.08em);
  position: absolute;
  display: inline-block;
          user-select: none;
     -moz-user-select: none;
  -webkit-user-select: none;
      -ms-user-select: none;
  z-index: 1;
}
.hdhenoji {
  position: relative;
  width: 1em;
  white-space: pre;
  display: inline-block;
  padding: 0 0.5em;
  color: rgba(51, 51, 51, 0);
}
.hdhenoji:after {
  position: absolute;
  color: rgba(51, 51, 51, 1.0);
  content: "゜";
  top: 0.07em;
  left: 0.6em;
  z-index: 1;
}

/* 汉化注：__{{文本}}__ 波浪线 */
span[style="text-decoration: underline;"] tt {
  position: relative;
  text-decoration: none;
}
span[style="text-decoration: underline;"] tt::before {
  content: "";
  position: absolute;
  top: 90%;
  width: 100%;
  height: 0.25em;
  background: -webkit-linear-gradient(315deg, transparent, transparent 45%, #000000, transparent 55%, transparent 100%), -webkit-linear-gradient(45deg, transparent, transparent 45%, #000000, transparent 55%, transparent 100%);
  background:         linear-gradient(135deg, transparent, transparent 45%, #000000, transparent 55%, transparent 100%), linear-gradient(45deg, transparent, transparent 45%, #000000, transparent 55%, transparent 100%);
  background-size: 0.5em 0.5em;
  background-repeat: repeat-x, repeat-x;
}
.namisen, .bolang {
  position: relative;
}
.namisen::before, .bolang::before {
  content: "";
  position: absolute;
  top: 90%;
  width: 100%;
  height: 0.25em;
  background: -webkit-linear-gradient(315deg, transparent, transparent 45%, #000000, transparent 55%, transparent 100%), -webkit-linear-gradient(45deg, transparent, transparent 45%, #000000, transparent 55%, transparent 100%);
  background:         linear-gradient(135deg, transparent, transparent 45%, #000000, transparent 55%, transparent 100%), linear-gradient(45deg, transparent, transparent 45%, #000000, transparent 55%, transparent 100%);
  background-size: 0.5em 0.5em;
  background-repeat: repeat-x, repeat-x;
}
.warning {
  position: relative;
}
.warning::before {
  content: "";
  position: absolute;
  top: 90%;
  width: 100%;
  height: 0.25em;
  background: -webkit-linear-gradient(315deg, transparent, transparent 45%, #ff0000, transparent 55%, transparent 100%), -webkit-linear-gradient(45deg, transparent, transparent 45%, #ff0000, transparent 55%, transparent 100%);
  background:         linear-gradient(135deg, transparent, transparent 45%, #ff0000, transparent 55%, transparent 100%), linear-gradient(45deg, transparent, transparent 45%, #ff0000, transparent 55%, transparent 100%);
  background-size: 0.5em 0.5em;
  background-repeat: repeat-x, repeat-x;
}

/* 汉化注：完全覆写了等宽 {{tt}} 环境，慎用 */
tt {
  font-family: var(--body-font, var(--page-font, arial, helvetica, sans-serif));
  font-size: 1em;
  display: inline-table;
  text-align: center;
  white-space: nowrap;
  line-height: 1;
  height: 1em;
  vertical-align: calc(1em - 1px);
  text-indent: 0%;
}

/* 汉化注：Ruby 的简写 */
tt > sup {
  display: table-header-group;
  font-size: 0.6em;
  line-height: 1.1;
  text-align: center;
  white-space: pre;
}

/* 汉化注：{{,,文本,,}} 下附芝麻粒/顿号 */
tt > sub {
  -webkit-text-emphasis-style: filled sesame;
          text-emphasis-style: filled sesame;
/*  text-emphasis-position: under right;
  -webkit-text-emphasis-position: under right; */
  font-size: 1em;
  vertical-align: calc(-1em + 1px);
}

/* 汉化注：{{//文本//}} 下附奇怪符号 */
tt > em {
  font-size: 12.8px;
  font-style: normal;
  -webkit-text-emphasis-style: filled dot;
          text-emphasis-style: filled dot;
/*  text-emphasis-position: under right;
  -webkit-text-emphasis-position: under right; */
  vertical-align: calc(-1em + 1px);
}
tt > em {
  /* For the environments which comply with CSS3. */
  font-style: normal;
  font-weight: 400;
  background-image: url(data:image/png;base64,), none;
  background-repeat: repeat-x;
  padding: 0.5em 0 0;
  background-color: transparent;
  background-clip: padding-box, content-box;
  background-size: 1em 1.3em, auto;
}
.reddel {
  position: relative;
}
.reddel:before {
  content: " ";
  display: block;
  position: absolute;
  right: 0;
  left: 0;
  top: 40%;
  bottom: 40%;
  border-top: 1px solid red;
  border-bottom: 1px solid red;
}

/* 汉化注：圈点；中文类改为下方 */
.kenten-b, .qd-b {
  -webkit-text-emphasis-style: filled dot;
          text-emphasis-style: filled dot;
}
.kenten-c, .qd-c {
  -webkit-text-emphasis-style: filled circle;
          text-emphasis-style: filled circle;
}
.kenten-janome, .qd-quan {
  -webkit-text-emphasis-style: filled double-circle;
          text-emphasis-style: filled double-circle;
}
.kenten-t, .qd-t {
  -webkit-text-emphasis-style: filled triangle;
          text-emphasis-style: filled triangle;
}
.kenten-goma, .qd-dun {
  -webkit-text-emphasis-style: filled sesame;
          text-emphasis-style: filled sesame;
}
.kenten-wb, .qd-wb {
  -webkit-text-emphasis-style: open dot;
          text-emphasis-style: open dot;
}
.kenten-wc, .qd-wc {
  -webkit-text-emphasis-style: open circle;
          text-emphasis-style: open circle;
}
.kenten-doublec, .qd-doublec {
  -webkit-text-emphasis-style: open double-circle;
          text-emphasis-style: open double-circle;
}
.kenten-wt, .qd-wt {
  -webkit-text-emphasis-style: open triangle;
          text-emphasis-style: open triangle;
}
.kenten-wgoma, .qd-wdun {
  -webkit-text-emphasis-style: open sesame;
          text-emphasis-style: open sesame;
}
.kenten-hoshi, .qd-xing {
  -webkit-text-emphasis-style: "★";
          text-emphasis-style: "★";
}
.kenten-kane, .qd-qian {
  -webkit-text-emphasis-style: "¥";
          text-emphasis-style: "¥";
}
.qd-b, .qd-c, .qd-quan, .qd-t, .qd-dun, .qd-wb, .qd-wc, .qd-doublec, .qd-wt, .qd-wdun, .qd-xing, .qd-qian {
  text-emphasis-position: under right;
  -webkit-text-emphasis-position: under right;
}
.bousen, .overline {
  text-decoration: overline;
}


/* thank you tonootto */
div.pseudo-tags ul {
  font-size: 85%;
  border-top: 1px solid #bbbbbb;
  display: inline-block;
  margin: 10px 0 0;
  padding: 0 2px;
  list-style: none;
}
div.pseudo-tags li {
  color: #bb0011;
  text-decoration: none;
  display: inline-block;
  margin: 0 1px;
  cursor: pointer;
}
div.pseudo-tags li:hover {
  text-decoration: underline;
}

/* 汉化注：改稿标注 */
#page-content span[style$="new"] {
  display: inline-block;
  background: #eaffea;
}
#page-content span[style$="new"]::before {
  padding-left: 5px;
  content: "+ ";
  color: #55cc55;
}
#page-content span[style$="new"]::after {
  content: " ";
  padding-right: 5px;
}
#page-content span[style$="old"] {
  display: inline-block;
  background: #ffecec;
}
#page-content span[style$="old"]::before {
  padding-left: 5px;
  content: "- ";
  color: #da5757;
}
#page-content span[style$="old"]::after {
  content: " ";
  padding-right: 5px;
}
#page-content .new {
  display: inline-block;
  margin-right: 5px;
  background: #eaffea;
}
#page-content .new::before {
  padding-left: 5px;
  content: "+ ";
  color: #55cc55;
}
#page-content .new::after {
  content: " ";
  padding-right: 5px;
}
#page-content .old {
  display: inline-block;
  margin-right: 5px;
  background: #ffecec;
}
#page-content .old::before {
  padding-left: 5px;
  content: "- ";
  color: #da5757;
}
#page-content .old::after {
  content: " ";
  padding-right: 5px;
}
[[/module]]
