content:
[[module css]]
@import url('https://fonts.googleapis.com/css2?family=Noto+Serif+JP:wght@500&display=swap');
@import url('http://scpko.wdfiles.com/local--files/unfont/UnBatangBold.css');
@import url('http://scpko.wdfiles.com/local--files/unfont/UnShinmun.css');

body {
  min-width: 320px;
}

#page-content .koigare-header {
    display: flex;
    flex-direction: row;
    flex-wrap: nowrap;
    justify-content: space-between;
    align-items: baseline;
    margin: 0;
    padding: 0;
    width: 100%;
    height: 27.5px;
    border: solid 1.25px black;
}

#page-content .koigare-header,
#page-content .koigare-header * {
    box-sizing: border-box;
    line-height: 1;
}

#page-content .koigare-header > .item {
    flex-grow: 1;
    flex-shrink: 1;
    font-family: 'UnBatangBold', 'Noto Serif JP', serif;
    padding: 0 2px;
    font-size: 25px;
    text-align: center;
}

@media (max-width: 950px) {
    #page-content .koigare-header {
        height: 25.5px;
    }

    #page-content .koigare-header > .item {
        font-size: 23px;
    }
}

@media (max-width: 880px) {
    #page-content .koigare-header {
        height: 22.5px;
    }

    #page-content .koigare-header > .item {
        font-size: 20px;
    }
}

@media (max-width: 830px) {
    #page-content .koigare-header {
        height: 20.5px;
    }

    #page-content .koigare-header > .item {
        font-size: 18px;
    }
}

@media (max-width: 767px) {
    #page-content .koigare-header {
        height: calc(100vw / 35 + 5px);
    }

    #page-content .koigare-header > .item {
        font-size: calc(100vw / 35);
    }
}

#page-content .koigare-header > .item:nth-child(2),
#page-content .koigare-header > .item:nth-child(4),
#page-content .koigare-header > .item:nth-child(6),
#page-content .koigare-header > .item:nth-child(8) {
    border-right: solid 1px black;
    border-left: solid 1px black;
}

#page-content .koigare-header > .item:nth-child(10) {
    border-left: solid 1px black;
}

#page-content .koigare-header > .item.{$category} {
    border-bottom: solid 2px red;
}

#page-content .koigare-image {
    margin: 1px 0 0 0;
    padding: 0;
    width: 100%;
    height: auto;
    position: relative;
    overflow: hidden;
    font-family: 'UnShinmun';
}

#page-content .koigare-image img {
    width: 100%;
}

#page-content .koigare-image > .image-desc {
    display: flex;
    flex-direction: row;
    flex-wrap: nowrap;
    justify-content: space-between;
    align-items: center;
    position: absolute;
    bottom: 0;
    width: 100%;
    background-color: rgba(0,0,0,0.3);
}

#page-content .koigare-image > .image-desc > .image-left,
#page-content .koigare-image > .image-desc > .image-right {
    flex-basis: auto;
    flex-grow: 1;
    flex-shrink: 1;
    padding: 0.25em 1em;
    color: white;
    text-shadow: 2px 2px 2px black;
}

#page-content .koigare-image > .image-desc > .image-left {
    max-width: 70%;
}

#page-content .koigare-image > .image-desc > .image-right {
    text-align: right;
}

@media (max-width: 782px) {
  #page-content .koigare-image > .image-desc > * {
      font-size: 90%;
  }
}

@media (max-width: 767px) {
  #page-content .koigare-image > .image-desc > * {
      font-size: calc( 70vw / {$param} );
  }
}
[[/module]]
[[div_ class="koigare-header"]]
[[div_ class="item saishin"]]
최신
[[/div]]
[[div_ class="item kokunai"]]
국내
[[/div]]
[[div_ class="item kokusai"]]
국제
[[/div]]
[[div_ class="item keizai"]]
경제
[[/div]]
[[div_ class="item geinou"]]
예능
[[/div]]
[[div_ class="item sports"]]
스포츠
[[/div]]
[[div_ class="item seikatsu"]]
생활
[[/div]]
[[div_ class="item kagaku"]]
과학
[[/div]]
[[div_ class="item shinpi"]]
신비
[[/div]]
[[div_ class="item shuukyou"]]
종교
[[/div]]
[[/div]]
[[div_ class="koigare-image"]]
[[image {$image}]]
[[div_ class="image-desc"]]
[[div_ class="image-left"]]
[[size {$titlesize}]]{$title}[[/size]]
{$date}
[[/div]]
[[div_ class="image-right"]]
{$by}
[[/div]]
[[/div]]
[[/div]]
