[[code type="css"]]
/* ========< Free DLC >========*/
div.blockquote {
  border: solid rgba(0,0,0,0.5) 1px;
  box-shadow: 2px 2px 5px rgb(0 0 0 / 10%);
  background-color: transparent;
}
blockquote {
  background-color: transparent;
  border: none;
  border-left: solid 4px rgba(0,0,0,0.7);
  padding: 1px 1em;
  margin: 1.5em 0.5em;
}

#page-content .collapsible-block>div.collapsible-block-folded>a:not(.system-pagestate-ctrl a, .info-container a)::before,
#page-content .folded>.colmod-link-top>.foldable-list-container>a:not(.system-pagestate-ctrl a)::before {
    content: "▶ ";
}
#page-content .collapsible-block>div.collapsible-block-unfolded>div.collapsible-block-unfolded-link:last-child>a:not(.system-pagestate-ctrl a, .info-container a)::before,
#page-content .unfolded>.colmod-link-end>.foldable-list-container>a:not(.system-pagestate-ctrl a)::before {
    content: "△ ";
}
#page-content .collapsible-block>div.collapsible-block-unfolded>div.collapsible-block-unfolded-link:first-child>a:not(.system-pagestate-ctrl a, .info-container a)::before,
#page-content .unfolded>.colmod-link-top>.foldable-list-container>a:not(.system-pagestate-ctrl a)::before {
    content: "▽ ";
}
[[/code]]

[[code type="css"]]
/* <Navla component : Center> */
.sidebox {
    right: calc(((100vw - 9px - 100%) / 2) * -1);
}

.tocbox {
    right: unset;
    left: calc(((100vw - 9px - 100%) / 2) * -1);
}

@media all and (max-width: 479px) {
.sidebox {
    right: calc(((100vw - 100%) / 2) * -1);
}
.tocbox {
    right: unset;
    left: calc(((100vw - 100%) / 2) * -1);
}
}
@media (min-width: 1289px) {
.sidebox, .tocbox {
    max-width: calc((100vw - 100%)/2 - 50px) !important;
    padding: 10px;
    max-height: 60vh;
}
.sidebox:before, .tocbox:before {
    opacity: 0;
}
.sidebox *, .tocbox * {
    opacity: 1;
}
.sidebox:hover, .tocbox:hover {
    max-width: calc((100vw - 100%)/2 - 50px) !important;
    padding: 10px;
}
}
@media (min-width: 1980px) {
.sidebox, .sidebox:hover {
    max-width: 600px !important;
}
}
[[/code]]

