content:
[[iftags [[#ifexpr {$fixed-line} == (true) | - ]]]]
[[module css]]
/*
========================
笔记本组件 响应行
========================
*/
@import url("http://scp-wiki-cn.wikidot.com/component:note-source/code/3");
[[/module]]
[[/iftags]]



[[iftags [[#ifexpr {$self-defining} == (true) | - ]]]]
[[module CSS]]
/*
========================
自定义样式
========================
*/
:root{   
--cover-color000:0.0625rem 0.0625rem 0 rgba(var(--cl-a1),1), 0.125rem 0.125rem 0 rgba(var(--cl-a2),1), 0.1875rem 0.1875rem 0 rgba(var(--cl-a2),1), 0.25rem 0.25rem 0 rgba(var(--cl-a3),1), 0.3125rem 0.3125rem 0 rgba(var(--cl-a2),1), 0.375rem 0.375rem 0 rgba(var(--cl-a2),1), 0.4375rem 0.4375rem 0 rgba(var(--cl-a2),1), 0.58rem 0.8rem 0 rgba({$cover-color});

--line-color000:linear-gradient(to top ,transparent 0%, {$line-color}, transparent 5%, {$line-color} 11%,transparent 12%,transparent 20%);
}

@media (max-width: 495px) {
.bks-{$name}  .note-page {
    font-size:{$phone-size};
}
}

.bks-{$name} .note-wrap{
   box-shadow:{$book-cover};
    color: {$text-color};
    background-color: {$page-color};
}

.bks-{$name} .note-bookmark{
background-size:{$mark-size};
background-position:{$mark-position};
}


.bks-{$name}  .note-page {
     font-family: {$font-family}, sans-serif;
     background-image: {$page-line};
}

.bks-{$name} .note-bookmark{
background-position: 11rem 0;
background-position: {$mark-position};
}

.note-left .note-bookmark{
  background-size: 0;
  background-size: {$mark-left-size};
}

.note-right .note-bookmark{
  background-size: 0;
  background-size: {$mark-right-size};
}


.bks-{$name} .reserve-separator:after ,
.bks-{$name} .note-leaf .note-page:before {
  color:{$color-ridge-cover};
}

/* -------- 底角 ------------ */

.bks-{$name} .reserve-separator::after,
.bks-{$name} .note-leaf .note-page::after {
    color: {$color-ridge-vacancy};
}

.bks-{$name} .reserve-separator::before, 
.bks-{$name} .note-leaf .note-page::before {
    color: {$color-ridge-cover};
}

/* --------撕页------------ */

.bks-{$name} .reserve-chewpaper:after,
.bks-{$name} .reserve-chewpaper:before{    
    width: {$paper-chew-size};
    mask-size: {$paper-chew-size};
    -webkit-mask-size: {$paper-chew-size};
    background: {$paper-chew-size};
}

.bks-{$name} .reserve-chewpaper:before{
    left: {$paper-chew-size};
}


[[/module]]
[[/iftags]]


[[iftags [[#ifexpr {$fixed-line} == (true) | - ]]]]
[[module css]]
/*
========================
笔记本组件 响应行
========================
*/
@import url("http://scp-wiki-cn.wikidot.com/component:note-source/code/3");
[[/module]]
[[/iftags]]


[[div id="{$name}" class="note-nobody  note-cover bks-{$name} " style="width:{$pc-width};font-size:{$texts-size};font-family:{$texts-typeface}, sans-serif;" ]]
[[div class="note-wrap note-line  chew-paper-{$chew-paper}"  ]]
[[div class="note-page note-left" ]]
[[div  class="note-bookmark"]]
