content:
[[iftags [[#ifexpr {$self-defining} == (true) | - ]]]]
[[module CSS]]
:root{   
--cover-color000:0.0625rem 0.0625rem 0 rgba(var(--cl-a1),1), 0.125rem 0.125rem 0 rgba(var(--cl-a2),1), 0.1875rem 0.1875rem 0 rgba(var(--cl-a2),1), 0.25rem 0.25rem 0 rgba(var(--cl-a3),1), 0.3125rem 0.3125rem 0 rgba(var(--cl-a2),1), 0.375rem 0.375rem 0 rgba(var(--cl-a2),1), 0.4375rem 0.4375rem 0 rgba(var(--cl-a2),1), 0.58rem 0.8rem 0 rgba({$cover-color});
}

@media (max-width: 495px) {
   background-size: 90% 1.5rem;
    font-size:{$phone-size};
}
}



.bks-{$name} .note-wrap{
  box-shadow:{$book-cover};
    color: {$text-color};
    background-color: {$page-color};
}



.bks-{$name} .note-bookmark{
font-family: {$font-family};
background-size:{$mark-size};
background-position:{$mark-position};
}


.bks-{$name} .reserve-separator:after ,
.bks-{$name} .note-leaf .note-page:before {
 color:{$color-ridge-cover};
}

.bks-{$name} .reserve-separator:before , 
.bks-{$name} .note-leaf .note-page:after {
    color:{$color-ridge-vacancy};
}


.bks-{$name}  .note-page {
     font-family: {$font-family};
     background-image: {$page-line};
}

[[/module]]
[[/iftags]]

[[div id="{$name}" class="note-nobody  note-cover  bks-{$name}"  ]]
[[div class="note-wrap note-leaf"  ]]
[[div class="note-page " ]]
[[div  class="note-bookmark"]]
