content:
[[module css]]

@import url('https://fonts.googleapis.com/css?family=Nanum+Myeongjo&display=swap');
@import url('https://fonts.googleapis.com/css?family=Crimson+Pro&display=swap');

.notecontainer{
    background-image: url("	/local--files/component:note-page/page01.jpg");
    background-repeat: no-repeat;
    width: 660px;
    height: 850px;
    margin: 0px;
}

.notecontent{
    font-family: 'Nanum Myeongjo', 'Crimson Pro';
    font-size: 1em;
    color: #000;
    width: 90%;
    height: 96%;
    padding-top: 2%;
    margin-left: 5%;
}

.notetitle{
    margin-top: 1%;
    height: 1.8em;
    width: 28%;
    font-family: 'Nanum Myeongjo', 'Crimson Pro';
    font-width: bold;
    font-size: 1.8em;
    text-align: left;
    color: #AE9250;
    border-bottom: 0.3em solid #AE9250;
    padding: 0.5em 0.5em 0.2em 0.5em;
    margin-left: 5%;
}

@media (max-width: 767px){
     .notecontainer{
         background-size: 100% 100%;
}
}


[[/module]]


[[div class="notecontainer"]]
[[div class="notetitle"]]
{$title}
[[/div]]
[[div class="notecontent"]]
{$content}
[[/div]]
[[/div]]
