[[iftags [[#ifexpr {$fixed-line} == (true) | - ]]]]
[[module css]]
/*
========================
笔记本组件 响应行
========================
*/
@import url("http://scp-wiki-cn.wikidot.com/component:note-source/code/3");
[[/module]]
[[/iftags]]



[[iftags [[#ifexpr {$self-defining} == (true) | - ]]]]
[[module CSS]]
/*
========================
自定义样式
========================
*/

:root{

--line-color-{$line-color}:linear-gradient(to top ,transparent 0%,#{$line-color} 1%, transparent 5%, #{$line-color} 11%,transparent 12%,transparent 20%);
----line-color-no:none;
----line-color-none:none;
--paper-image:($paper-image);
}


/* 行线颜色，放上方以让下文顶替 */

.note-chew-{$name}  .note-paper-midner {
    background-image: {$page-line};
    background-image: var(--line-color-{$line-color});
}


/**/
.note-chew-{$name} {
    -webkit-filter:{$paper-wrap-shadow};
    filter: {$paper-wrap-shadow};  
}

.note-chew-{$name}  .note-paper-mid,
.note-chew-{$name} .note-paper-left,
.note-chew-{$name} .note-paper-right{
    background-color: {$page-color};
    color:{$text-color};
}


.note-chew-{$name} .note-paper-leftner,
.note-chew-{$name} .note-paper-rightner{
    background: {$cover-leftner};
    background: {$cover-both};
       background: {$cover-allner};

}

.note-chew-{$name} .note-paper-rightner{
    background: {$cover-rightner};
    background: {$cover-bothner};
    background: {$cover-allner};
    background: {$cover-allner};

}

.note-chew-{$name}  .note-paper-midner{
    background: {$cover-midner};
}


.note-chew-{$name} {
    -webkit-filter:{$paper-wrap-shadow};
    filter: {$paper-wrap-shadow};  
}


.note-chew-{$name}   .note-paper-mid div {
    font-family:{$font-family};
    font-size: {$font-size}; 
    text-align:{$text-align}; 
   color:{$text-color}; 
}

.note-chew-{$name}   .note-paper-mid p{
 -webkit-filter:{$filter-text};
filter:{$filter-text};
}

.note-chew-{$name} .note-paper-main div{
    min-width:{$paper-chew-size};
}

.note-chew-{$name} .chew-paper-{$chew-paper}   .note-paper-left ,
.note-chew-{$name} .chew-paper-{$chew-paper}  .note-paper-right{ 
 -webkit-mask-size: {$paper-chew-size};
         mask-size:{$paper-chew-size};
}


.note-chew-{$name}  .note-paper-left{
  background:{$background-left};
  background:{$background-both};
}

.note-chew-{$name}  .note-paper-mid{
  background:{$background-mid};
  padding: 0rem;
}

.note-chew-{$name}  .note-paper-right{
  background:{$background-right};
  background:{$background-both};
}

.note-chew-{$name}  .note-paper-unset111    .note-paper-main{
  background:{$background-main};
 overflow:hidden;/**/
}

/* 等宽字体代码 */
.note-nobody.note-chew-{$name} tt{
    color:{$monospace-color};
     left:{$monospace-left};
     top:{$monospace-top};
    font-family:{$monospace-family};
    font-size: {$monospace-size}; 
}

.note-nobody.note-chew-{$name} tt{
    color:{$monospace-color};
     left:{$monospace-left};
     top:{$monospace-top};
    font-family:{$monospace-family};
    font-size: {$monospace-size}; 
}


.preset-style-oldletter .note-paper-midner tt span[style*="text-decoration: underline"] { 
     left:{$monospace-left-hr};
}

/**/

@media (max-width: 495px) {  

 .note-chew-{$name}  .note-paper-unset111     .note-paper-main div{
    min-width:{$paper-chew-mobsize};
}

.note-chew-{$name}  .chew-paper-{$chew-paper} .note-paper-left ,
.note-chew-{$name}  .chew-paper-{$chew-paper}  .note-paper-right{ 
 -webkit-mask-size: {$paper-chew-mobsize};
         mask-size:{$paper-chew-mobsize};
}

note-chew-{$name}  .note-paper-unset111  .note-paper-leftner, 
.note-chew-{$name}  .note-paper-unset111   .note-paper-rightner {
    background: {$background-inner};
    width:auto;
    height:auto; 
    clear:none ;
}
}

/* .note-chew-{$name} .note-paper-unset111   .note-paper-under */
.note-chew-{$name}  .note-paper-unset111  .note-paper-leftner, 
.note-chew-{$name} .note-paper-unset111   .note-paper-rightner {
    background: {$background-inner};
    width:100%;
    height:100%; 
    clear:none ;
}

.note-chew-{$name}  .note-paper-unset111  .note-paper-under {
    background: {$background-inner};
}


/* 最小宽高 */
.note-chew-{$name} .note-paper-unset111  .note-paper-main{
   min-width:{$min-width};
}

.note-chew-{$name}  .note-paper-unset111  .note-paper-midner {
   min-width:{$min-mob-width};
}

@media (max-width: 495px) {  

.note-chew-{$name}  .note-paper-unset111  .note-paper-main{
   min-width:{$min-mob-width};
}

.note-chew-{$name}  .note-paper-unset111  .note-paper-mid{
  min-height:{$min-mob-height};
}
}

/* 边缘文字 */
.note-chew-{$name} .note-paper-rightner p,
.note-chew-{$name} .note-paper-leftner p{
   padding-top:{$side-text-top};
   padding-bottom:{$side-text-bottom};
}

.note-chew-{$name} .note-paper-leftner  p{
   display:{$side-text-before};
}

.note-chew-{$name} .note-paper-rightner p{
   display:{$side-text-after};
}


/*-----------卷轴------------------*/
/* 隐藏卷轴一边的卷，前后（top或bottom）  */

.note-chew-{$name}  .accessory-scroll-horiz  .note-reserve-{$hidden-scroll-side},
.note-chew-{$name}  .accessory-scroll-verti   .note-reserve-{$hidden-scroll-side}  {
     display:none;
}


/*-----------纸堆------------------*/

.accessory-pile  .note-paper-unset222:before,
.accessory-pile .note-paper-unset222:after,
.accessory-pile  .note-paper-unset333:before,
.accessory-pile .note-paper-unset333:after,
.accessory-pile .chew-paper-verti .crease-set-corner:before，
.accessory-pile .chew-paper-verti .crease-set-corner:after，
.accessory-pile .chew-paper-horiz .crease-set-corner:before，
.accessory-pile .chew-paper-horiz .crease-set-corner:after {
    background-color:rgba(243,243,243,1);
    background-color:{$page-color};
    background-color:{$pile-color};
}

/* 无效动画 */

.note-nobody[class*="piling"] .note-paper-unset111,
.note-nobody[class*="piling"] .note-paper-unset222,
.note-nobody[class*="piling"] .note-paper-unset333,
.note-nobody[class*="piling"] .note-paper-main{
    opacity: 0;
    animation-name: Opac0to1；
    animation:{$piling-animation};
}


.note-nobody[class*="piling"] .note-paper-unset111{
 animation-delay: calc(0.1 * {$piling-delay});
}

.note-nobody[class*="piling"] .note-paper-unset222{
 animation-delay: calc(0.1 * {$piling-delay});
}


.note-nobody[class*="piling"] .note-paper-unset333{
 animation-delay: calc(1.5 * {$piling-delay});
}

.note-nobody[class*="piling"] .note-paper-main{
 animation-delay: calc(2.5 * {$piling-delay});
}




[[/module]]
[[/iftags]]


[[iftags [[#ifexpr {$header-footer} == (true) | - ]]]]
[[module CSS]]

/*----------- 页眉页尾 ------------------*/

.note-chew-{$name} .note-paper-upon{
    color:{$upon-color};
    font-size:{$upon-size};
    margin:{$upon-margin};
}

.note-chew-{$name} .note-paper-upon p{
    margin:{$upon-margin};
    font-family:{$upon-typeface} , sans-serif;
}

.note-chew-{$name} .note-paper-under{
    color:{$under-color};
    font-size:{$under-size};
    margin:{$under-margin};
}

.note-chew-{$name} .note-paper-under p{
    margin:{$under-margin};
    font-family:{$under-typeface} , sans-serif;
}
[[/module]]
[[/iftags]]


[[div class="note-nobody note-paper-wrap  note-chew-{$name}  preset-style-{$preset-style} " style="width:{$pc-width};" ]]
[[div class="note-paper-unset111  accessory-{$accessory}"]]
[[div class="note-paper-unset222  chew-paper-{$chew-paper} " ]]
[[div class="note-paper-unset333  crease-left-{$crease-left}  crease-right-{$crease-right} "]]
[[div class="note-paper-setbox crease-set-corner" ]]
[[div class="note-reserve-top"]]
[[/div]]
[[div class="note-paper-main "]]
[[div class="note-paper-left note-paper-base" ]]
[[div class="note-paper-leftner" ]]
{$side-text}
[[/div]]
[[/div]]
[[div  class="note-paper-mid note-paper-base"  ]]
[[div class="note-paper-upon  upon-set-{$upon-set} " ]]
{$upon-text}
[[/div]]
[[div  class="note-paper-midner"  style="font-size:{$texts-size};font-family:{$texts-typeface}, sans-serif;"]]
