[[module CSS]]
@import url(https://scp-wiki-cn.wikidot.com/component:note-source/code/1);

#content-wrap #main-content {
    min-width:{$main-content};
}


#page-content {
    min-width:{$page-content};
}

#page-content,
#content-wrap #main-content {
    min-width:{$main-page-width};
}


#edit-page-form{
    margin: 0 auto;
}


.note-wrap .note-bookmark{
        background-size:{$my-bookmark};
}

@media (max-width: 495px) {
#content-wrap #main-content {
min-width: 0rem;
}

/*
==================
全局自定义选项
==================
*/

.note-nobody *{
    font-size:{$texts-size};
    font-family:{$texts-typeface}, sans-serif;
}

@media (max-width: 500px) {
.note-nobody * {
    font-size:{$texts-size-mob};
    font-family:{$texts-typeface-mob}, sans-serif;
}
}

[[/module]]


[[iftags +_源代码]]
