}

.note-nobody[class*="piling"] .note-paper-main{
 animation-delay: calc(2.5 * var(--piling-delay)); 
}




@keyframes Opac0to1{
    0% {
                     opacity: 0;
    }
    100% {
                      opacity: 1;
    }
}

