content:
[[iftags [[#ifexpr {$self-defining} == (true) | - ]]]]
[[module css]]
/*
========================
便利贴 自定义选项
========================
*/

#page-content .note-tip-{$name} .note-tip-set{
  background-image:linear-gradient(-45deg,transparent 0%, transparent 0rem ,{$tip-color} 0rem, {$tip-color} 100%);

color:{$texts-color};
}  

#page-content  .note-tip-{$name} .note-tip-set.tip-crease-right{
  background-image:linear-gradient(-45deg,transparent 0%, transparent 1.6rem ,{$tip-color} 1.7rem,{$tip-color} 100%);
}  

#page-content  .note-tip-{$name} .note-tip-set.tip-crease-left{
  background-image:linear-gradient(45deg,transparent 0%, transparent 1.6rem ,{$tip-color} 1.7rem, {$tip-color} 100%);
}  

#page-content .note-tip-{$name} .tip-crease-left .note-tip-cover:before {
   left: 0%;
  border-right:2.1rem solid {$tip-color};
  border-bottom: 2.5rem solid transparent;
}

#page-content .note-tip-{$name} .tip-crease-right .note-tip-cover:before {  
  border-left:2.1rem solid {$tip-color};
}


.note-tip-{$name}.note-tip-wrap{

margin-left:{$position-left};
margin-right:{$position-right};

-ms-transform:rotate({$rotate}deg); 	 
-moz-transform:rotate({$rotate}deg);  
-webkit-transform:rotate({$rotate}deg); 
-o-transform:rotate({$rotate}deg); 
    transform:rotate({$rotate}deg);
}



.note-tip-{$name} .note-tip-stick{
height:{$stick-height}; 
}

@media (max-width: 495px) {
 .note-tip-wrap{
   margin-left:{$position-left-mob} !important;
   margin-right: {$position-right-mob} !important;
  }
}
[[/module]]
[[/iftags]]

[[div class="pre-note" style="/*{$fix-mobwith};*/"]]
[[/div]]

[[div class="note-tip-{$name} note-tip-wrap " style="width:{$width}; float:{$float};"]]

[[div class="note-tip-set tip-crease-{$crease}" ]]
[[div class="note-tip-cover"]]

[[div class="note-tip-stick"  ]]
[[/div]]

[[div class="note-tip-content"]]
