[[module CSS]]
:root {
  --policy: #6FF;
  --information: #9CF;
  --meeting: #FF6;
  --after: #FE9933;
}
[[/module]]

[[table style="border-collapse:collapse; margin:auto; width:85%; --bg: var(--{$bg})"]]
[[row]][[cell style="height: auto; text-align:center; vertical-align: center; font-size:20px; font-weight:bold; background: var(--bg);line-height: 30px; word-break: keep-all;"]]
{$headline}
[[/cell]][[/row]]
[[row]][[cell style="text-align:right;"]]
{$date}
[[/cell]][[/row]]
[[/table]]
