content:
[[module CSS]]
@import url('https://fonts.googleapis.com/css?family=Montserrat:400');

:root {
  --more-text: "更多信息";
  --triangle-url: url(http://topia.wdfiles.com/local--files/cqb%3Apage-info-slider-deep/slider.png)
}

table.wiki-content-table { margin: 0 -1px 0 0; width: 100%; }
table.wiki-content-table:not(:nth-of-type(1)) { margin-top: -1px; }
table.wiki-content-table td { background-color: #fff; }
table.wiki-content-table th { background-color: #eee; }

.avatar-hover { z-index: 30; }

.page-box .table .shade { position: absolute; top: 0; left: 0; height: 100%; width: 100%; pointer-events: none; opacity: 0.1; background-color: none;}

.page-box { position: relative; width: 90%; margin: 0 auto 2em auto; z-index: 20; overflow: hidden; }
.page-box::after { content: ""; position: absolute; width: 100%; height: 100%; top: 0; left: 0; box-shadow: inset 0 0.5px 3px 1px rgba(0,0,0,0.3); z-index: 19; pointer-events: none; }

.table { position: relative; top: 0; left: 0; height: 100%; width: 100%; }

.page-cover { position: absolute; top: 0; left: 0; height: 100%; width: calc(100% - 93px); z-index: 10; background-color: white; display: flex; flex-direction: column; justify-content: space-around; padding: 20px 0 20px 25px; box-sizing: border-box; transform: translate(0,0); transition: transform 0.6s ease-in-out; will-change: transform; }
.page-cover::after { content: var(--more-text); position: absolute; top: 50%; right: -70px;  color: rgba(0,0,0,0.2); font-weight: bold; font-size: 0.8em; transform: translate(0,-50%); pointer-events: none; }
.page-cover::before { content: ""; position: absolute; top: 0; right: -102px; width: 103px; height: 100%; background-image: var(--triangle-url); background-position: center; pointer-events: none; }

@media only screen and (max-width: 767px) {
  .page-cover { display: none; }
}

.page-cover .cover-title { width: 100%; font-size: 50px; font-family: Montserrat,Verdana,sans-serif; white-space: nowrap; text-overflow: ellipsis; overflow: hidden; }
.page-cover .cover-title.medium { font-size: 30px; }
.page-cover .cover-title.long { font-size: 20px; }
.page-cover .cover-title a { background-color: rgba(255,255,255,0.6); position: relative; z-index: 1; }

.page-cover .cover-tagline { color: rgba(0,0,0,0.75); margin-bottom: 0; flex: 0; }
.page-cover .cover-title.no-truncate + .cover-info .cover-tagline { margin: 1em 0; }

.page-cover .cover-info .page-info { display: flex; }
.page-cover .cover-info .page-info div { color: rgba(0,0,0,0.5); margin: 0 6em 0 0; }

.page-box:hover .page-cover:not(:hover) { transform: translate(calc(-100% - 70px),0); }

.page-cover .cover-title.no-truncate { flex: 1; white-space: normal; }

.page-cover > img { max-height: 70%; max-width: 120px; position: absolute; top: 50%; right: 60px; transform: translate(50%,-50%); }

/* BLACK HIGHLIGHTER COMPATIBILITY */
table.wiki-content-table { border-collapse: collapse; }
table.wiki-content-table tbody { margin: 0; display: table-row-group; }
table.wiki-content-table th { color: black; }
table.wiki-content-table td { border-color: inherit; }
[[/module]]
