[[module CSS]]
:root{
  --logo: url(http://scpko.wdfiles.com/local--files/component%3Apride-logo-by-navla/pride.png);
  --logo: var(--logo-{$flag});
  --logo-{$flag}: url(http://scpko.wdfiles.com/local--files/component%3Apride-logo-by-navla/{$flag}.png);
}
[[/module]]

[[#if {$nvl} |  | [!-- ]]
[[#if {$bhl} |  | [!-- ]]
[[module CSS]]
/* BHL theme */
:root{
  --logo-image: var(--logo);
}
#header:after {
    content:"";
    position: absolute;
    top: 0;
    bottom: 0;
    right: 0;
    left: 0;
    filter: drop-shadow(5px 6px 3px rgba(0,0,0,0.7));
    background-image: var(--logo-image);
    background-repeat: no-repeat;
}
@supports (display:grid){
#header {
    background: none;
}
#header:after {
    background-position: left 0rem top 1.1875rem;
    background-size: auto calc(var(--header-height-on-desktop) - 1.7rem);
}
}
@media only screen and (max-width: 768px){
#header:after {
    background-position: left -1.6rem top 1rem;
    background-size: auto calc(var(--header-height-on-desktop) - 0.3rem);
}
}
div#extra-div-1 {
  background-image: var(--logo);
  opacity: 0.3;
  filter: drop-shadow(5px 6px 3px rgba(0,0,0,0.7));
}
[[/module]]
[!-- --]

[[#if {$nvl} |  | [!-- ]]
[[#if {$bhl} | [!-- | ]]
[[module CSS]]
/* Sigma theme */
div#header:after {
    content: "";
    position: absolute;
    top: 0;
    bottom: 0;
    right: 0;
    left: 0;
    filter: drop-shadow(5px 6px 3px rgba(0,0,0,0.7));
}
div#header {
    background: none;
}
div#header:after {
    background: 10px 40px no-repeat;
    background-image: var(--logo);
    background-size: 98px;
}
@media only screen and (max-width: 979px) {
div#header:after {
  background: var(--logo) 10px 50px no-repeat;
  background-size: 85px;
}
}

@media only screen and (max-width: 768px) {
div#header:after {
  background: var(--logo) 0px 51px no-repeat;
  background-size: 85px;
}
}
@media (max-width: 579px) {
div#header:after {
  background: var(--logo) 0px 58px no-repeat;
  background-size: 76px;
}
}
@media (max-width: 479px) {
div#header:after {
  background: var(--logo) 0px 67px no-repeat;
  background-size: 63px;
}
@media (max-width: 385px) {
div#header:after {
  background: var(--logo) 13px 67px no-repeat;
  background-size: 63px;
}

div#extra-div-1 {
  background-image: none;
  opacity: 1;
    filter: drop-shadow(5px 6px 3px rgba(0,0,0,0.7));
}

[[/module]]
[!-- --]

[[#if {$nvl} |  | [!-- ]]
[[#if {$BnP} |  | [!-- ]]
[[module CSS]]
/* Basalt&Penumbra&Blank theme */
:root{
  --lgurl: var(--logo);
}
#header {
  background: none;
}
[[/module]]
[!-- --]

[[#if {$flag} |  | [!-- ]]
[[#if {$nvl} | [!-- |  ]]
[[module CSS]]
/* Navla-style theme */
:root:lang(ko){
  --logo-image: var(--logo);
}
div#header-extra-div-1 {
    filter: invert(0) drop-shadow(5px 6px 3px rgba(0,0,0,0.7));
}
[[/module]]
[!-- --]
