/* å¤åˆ¶è‡ªä¸Šæ–‡ç»„ä»¶ä»£ç çš„ç¤ºä¾‹logo */

div#header {
    background-image: url("https://scp-wiki.wikidot.com/local--files/component:pride-logos/scp-default.png");
}
#header::before{
    position: absolute;
    content: '';
    width: 100%;
    height: 100%;
    background-image: url("https://scp-wiki.wikidot.com/local--files/component:pride-logos/scp-pride.png");
    background-repeat: no-repeat;
    background-position: inherit;
    background-size: inherit;
    -webkit-mask-image: linear-gradient(60deg, transparent 30%, black 60%);
    mask-image: linear-gradient(60deg, transparent 30%, black 60%);
    -webkit-mask-position: 10px 40px;
    mask-position: 10px 40px;
    -webkit-mask-size: 100px 100px;
    mask-size: 100px 100px;
}
@media (max-width: 479px) {
    #header::before {
        -webkit-mask-position: 0 5.5em;
        mask-position: 0 5.5em;
        -webkit-mask-size: 55px 55px;
        mask-size: 55px 55px;
    }
}
@media (min-width: 480px) and (max-width: 580px) {
    #header::before {
        -webkit-mask-position: 0.5em 4.5em;
        mask-position: 0.5em 4.5em;
        -webkit-mask-size: 66px 66px;
        mask-size: 66px 66px;
    }
}
@media (min-width: 581px) and (max-width: 767px) {
    #header::before {
        -webkit-mask-position: 1em 4em;
        mask-position: 1em 4em;
        -webkit-mask-size: 77px 77px;
        mask-size: 77px 77px;
    }
}
@media (min-width: 768px) and (max-width: 979px) {
    #header::before {
        -webkit-mask-position: 1em 4em;
        mask-position: 1em 4em;
        -webkit-mask-size: 88px 88px;
        mask-size: 88px 88px;
    }
}
@media (min-width: 979px) {
    #header {
        background-size: 100px 100px;
    }
}
