[[module CSS]]

/* 修复换行问题 */

.parade span, 
blockquote, div.blockquote, 
blockquote span, div.blockquote span, 
.rt, rt {
    word-break: keep-all;
}

.ruby, ruby, .rt, rt {
    white-space: normal;
}

/* 设定logo展示表的样式 */

.showcase tbody {
    background-image: repeating-linear-gradient(135deg, rgba(0, 0, 0, .1), rgba(0, 0, 0, .1) 15px, transparent 15px, transparent 30px);
}
.showcase table.wiki-content-table {margin: 0 auto;}
.showcase tr, .showcase td {
    text-align: center;
    font-weight: bold;
    border: 2px solid black;
    word-break: break-all;
    max-width: 12vw;
    min-width: 12vw;
}

/* 动态彩虹色文字 */

.parade {
    margin: 0.5em auto;
    text-align: center;
    font-family: 'Comic Sans MS', 'Comic Sans', cursive;
    color: transparent;
    -webkit-background-clip: text;
    background-clip: text;
    background-image: linear-gradient(45deg, 
rgb(255,0,0),
rgb(255,128,0),
rgb(255,255,0),
rgb(128,255,0),
rgb(0,255,0),
rgb(0,255,128),
rgb(0,255,255),
rgb(0,128,255),
rgb(0,0,255),
rgb(128,0,255),
rgb(255,0,255),
rgb(255,0,128),
rgb(255,0,0));
    background-size: 300%;
    -webkit-animation: rainbow 5s linear infinite;
    -moz-animation: rainbow 5s linear infinite;
    -o-animation: rainbow 5s linear infinite;
    animation: rainbow 5s linear infinite;
}

.parade p {line-height: 300%;}

@keyframes rainbow {
    0% {background-position: 0 0;}
    100% {background-position: 300% 0;}
}

/* 复制自上文组件代码的示例logo */

div#header {
    background-image: url("https://scp-wiki.wikidot.com/local--files/component:pride-logos/scp-default.png");
}
#header::before{
    position: absolute;
    content: '';
    width: 100%;
    height: 100%;
    background-image: url("https://scp-wiki.wikidot.com/local--files/component:pride-logos/scp-pride.png");
    background-repeat: no-repeat;
    background-position: inherit;
    background-size: inherit;
    -webkit-mask-image: linear-gradient(60deg, transparent 30%, black 60%);
    mask-image: linear-gradient(60deg, transparent 30%, black 60%);
    -webkit-mask-position: 10px 40px;
    mask-position: 10px 40px;
    -webkit-mask-size: 100px 100px;
    mask-size: 100px 100px;
}
@media (max-width: 479px) {
    #header::before {
        -webkit-mask-position: 0 5.5em;
        mask-position: 0 5.5em;
        -webkit-mask-size: 55px 55px;
        mask-size: 55px 55px;
    }
}
@media (min-width: 480px) and (max-width: 580px) {
    #header::before {
        -webkit-mask-position: 0.5em 4.5em;
        mask-position: 0.5em 4.5em;
        -webkit-mask-size: 66px 66px;
        mask-size: 66px 66px;
    }
}
@media (min-width: 581px) and (max-width: 767px) {
    #header::before {
        -webkit-mask-position: 1em 4em;
        mask-position: 1em 4em;
        -webkit-mask-size: 77px 77px;
        mask-size: 77px 77px;
    }
}
@media (min-width: 768px) and (max-width: 979px) {
    #header::before {
        -webkit-mask-position: 1em 4em;
        mask-position: 1em 4em;
        -webkit-mask-size: 88px 88px;
        mask-size: 88px 88px;
    }
}
@media (min-width: 979px) {
    #header {
        background-size: 100px 100px;
    }
}
/* 彩虹色版头，改编自Orison的SCP跨性别骄傲版式的动态版头 */

div#container-wrap{
    background-image: url("https://scp-wiki.wdfiles.com/local--files/theme%3Ascp-trans-pride/Trans-CSS-File.png");
}
#container{
    background: linear-gradient(to bottom, rgba(0,0,0,0) 400px, rgba(255,255,255,1) 400px);
}
#container-wrap-wrap{
    background-image: linear-gradient(to right, 
rgba(255,0,0,.5),
rgba(255,128,0,.5),
rgba(255,255,0,.5),
rgba(128,255,0,.5),
rgba(0,255,0,.5),
rgba(0,255,128,.5),
rgba(0,255,255,.5),
rgba(0,128,255,.5),
rgba(0,0,255,.5),
rgba(128,0,255,.5),
rgb(255,0,255,.5),
rgb(255,0,128,.5),
rgb(255,0,0,.5));
    background-size: 300% 100%;
    -webkit-animation: tint 10s linear infinite;
    -moz-animation: tint 10s linear infinite;
    -o-animation: tint 10s linear infinite;
    animation: tint 10s linear infinite;
}
@keyframes tint {
    0% {background-position: 0% 0;}
    100% {background-position: 300% 0;}
}
