[[module CSS]]

/* Set the first logo */

div#header {
    background-image: url("https://scp-wiki.wikidot.com/local--files/component:pride-logos/scp-{$code}.png");
    background-image: {$custom};
}

/* The second logo is created by a pseudo-element atop the first one */

#header::before{
    position: absolute;
    content: '';
    width: 100%;
    height: 100%;
    pointer-events: none; /* Disable clicking event */
    top: 0;
    left: 0;
    background-image: url("https://scp-wiki.wikidot.com/local--files/component:pride-logos/scp-{$code2}.png");
    background-image: {$custom2};
    background-repeat: no-repeat;
    background-position: inherit;
    background-size: inherit;

/* -webkit- prefix is set for maximum compatibility especially on mobile */

/* 1. Default divider */

    -webkit-mask-image: linear-gradient(to right, transparent 50%, black 50%);
    mask-image: linear-gradient(to right, transparent 50%, black 50%);

/* 2. Angled (non-vertical) divider */

    -webkit-mask-image: linear-gradient({$angle}deg, transparent 50%, black 50%);
    mask-image: linear-gradient({$angle}deg, transparent 50%, black 50%);

/* 3. User-customizable mask */

    -webkit-mask-image: {$mask};
    mask-image: {$mask};

/* Default mask position and size on desktop */

    -webkit-mask-position: 10px 40px;
    mask-position: 10px 40px;
    -webkit-mask-size: 100px 100px;
    mask-size: 100px 100px;
}
[[/module]]

[!-- -{$s10}
[[module CSS]]

/* Due to a bug, two hyphens "--" have a chance to be translated to a whole dash "â€”" in Wikidot, which will result in a code malfunction. Hence here does not use "--]" to disable the comment code, but "-]" instead. */

/* Sigma-10 responsive sizing */

@media (max-width: 767px) {
    #header::before {
        -webkit-mask-position: 8px 64%;
        mask-position: 8px 64%;
        -webkit-mask-size: calc(48px + 5%) calc(48px + 5vw);
        mask-size: calc(48px + 5%) calc(48px + 5vw);
    }
}
[[/module]]
[!-- --]

[!-- -{$s9}
[[module CSS]]

/* Sigma-9 responsive sizing */

@media (max-width: 479px) {
    #header::before {
        -webkit-mask-position: 0 5.5em;
        mask-position: 0 5.5em;
        -webkit-mask-size: 55px 55px;
        mask-size: 55px 55px;
    }
}
@media (min-width: 480px) and (max-width: 580px) {
    #header::before {
        -webkit-mask-position: 0.5em 4.5em;
        mask-position: 0.5em 4.5em;
        -webkit-mask-size: 66px 66px;
        mask-size: 66px 66px;
    }
}
@media (min-width: 581px) and (max-width: 767px) {
    #header::before {
        -webkit-mask-position: 1em 4em;
        mask-position: 1em 4em;
        -webkit-mask-size: 77px 77px;
        mask-size: 77px 77px;
    }
}
@media (min-width: 768px) and (max-width: 979px) {
    #header::before {
        -webkit-mask-position: 1em 4em;
        mask-position: 1em 4em;
        -webkit-mask-size: 88px 88px;
        mask-size: 88px 88px;
    }
}
[[/module]]
