[[module CSS]]

#side-bar {
  z-index: calc( ( 1 - {$alt} ) * 15 );
  margin-top: 16em ;
}

@media(min-width: 1040px) {
  #side-bar {
    left: 50vw;
    transform: translateX(-503px);
  }
}

@media(min-width: 980px) and (max-width: 1039px) {
  #side-bar {
    left: 2em;
  }
}

@media(max-width: 767px) {
  #side-bar {
    margin-top: 0;
  }
}

