[[module CSS]]

@import url('https://fonts.googleapis.com/css?family=Nanum+Pen+Script');

:root {
  --wd: 300px;
  --ht: 220px;
}

.redcard {
  position: absolute;
  top: var(--top);
  left: var(--left);
  right: var(--right);
  transform: translateX(var(--tx, 0)) rotate(var(--deg));
  width: var(--wd);
  height: var(--ht);
  background: #F48AAD;
  z-index: 50;
}

.redcard table {
  border: 3px solid #000;
  width: calc(var(--wd) - 1em);
  height: calc(var(--ht) - 1em);
  margin: 0.5em;
  max-width: calc(100% - 1em) !important;
  max-height: calc(100% - 1em) !important;
}

.redcard table td {
  border-color: #000;
  font-size: 120%;
  word-break: keep-all;
}

.redcard table td span {
  display: inline-block;
  font-family: 'Nanum Pen Script';
  font-size: 200%;
  font-style: italic;
  transform: translateY(0.2em);
  padding: 0 10px 0 5px;
  line-height: 0.1;
}

.redcard table tr:first-child td {
  font-size: 150%;
  font-weight: 900; 
}

.redcard.center1 table tr:first-child td {
  font-size: 180%;
}

.redcard > p > a {
  color: transparent;
  position: absolute;
  inset: 0;
}

.redcard.center1 {
  --tx: -50%;
}

.redcard.more1 {
  display: none;
}

@media (min-width: 768px) {
  :root {
    --wd: 500px;
    --ht: 300px;
  }
  .redcard table tr:first-child td {
    font-size: 200%;
  }
  .redcard.center1 table tr:first-child td {
    font-size: 250%;
  }
  .redcard table tr:not(:first-child) td {
    line-height: 1.8
  }
  .redcard.center1 {
    --tx: -30%;
  }
  .redcard.more1 {
    display: block;
  }
}

[[/module]]

